        {
            ptype: 'cellediting',
            clicksToEdit: 1,
            listeners: {
            	edit: function(editor, context, eOpts) {
            		var BOX_QTY = Number(context.record.data.BOX_QTY); 
            		var KG_QTY = Number(context.record.data.KG_QTY);
            		var BOX_WGT_UNIT = Number(context.record.data.BOX_WGT_UNIT);
            		var S_CON_PRC = Number(context.record.data.S_CON_PRC);
            		
            		var record = Ext.getCmp('myGrid').getSelectionModel().getSelection();
            		
            		if (context.column.dataIndex == 'S_CON_PRC'){
            			context.record.set('S_CON_AMT', Math.round(KG_QTY * S_CON_PRC));
            			context.record.set('BF_IN_AMT', Math.ceil(Math.round(KG_QTY * S_CON_PRC / 10) / 100000) * 100000);
            			
            		}
            		if (context.column.dataIndex == 'KG_QTY'){
            			context.record.set('BOX_QTY', KG_QTY / BOX_WGT_UNIT);
            			context.record.set('S_CON_AMT', Math.round(KG_QTY * S_CON_PRC));
            			context.record.set('BF_IN_AMT', Math.ceil(Math.round(KG_QTY * S_CON_PRC / 10) / 100000) * 100000);
            		}
            		if (context.column.dataIndex == 'BOX_QTY'){
            			
            			context.record.set('KG_QTY', Math.round(BOX_QTY * BOX_WGT_UNIT * 100) / 100);
            			context.record.set('S_CON_AMT', Math.round(Math.round(BOX_QTY * BOX_WGT_UNIT * 100) / 100 * S_CON_PRC));
            			context.record.set('BF_IN_AMT',  Math.ceil(Math.round(Math.round(BOX_QTY * BOX_WGT_UNIT * 100) / 100 * S_CON_PRC)/ 100000) * 100000);
            		}
            		
            		if (context.column.dataIndex == 'SALE_DEADLINE'){
            			var IN_DT = record[0].data['IN_DT'];
            			var SALE_DEADLINE = Number(context.record.data.SALE_DEADLINE);
            			
            			var SALE_OPTION_TYPE_CD = context.record.data.SALE_OPTION_TYPE_CD;
            			
            			if(SALE_OPTION_TYPE_CD == 'A'){
            				var SALE_DEADLINE_DT = new Date(IN_DT);
            				SALE_DEADLINE_DT.setDate(SALE_DEADLINE_DT.getDate() + SALE_DEADLINE);
            				
            				context.record.set('SALE_DEADLINE_DT', SALE_DEADLINE_DT);
            			}
            			else if (SALE_OPTION_TYPE_CD == 'B'){
            				var SALE_DEADLINE_DT = new Date(context.record.data.CONTRACT_DT);
            				SALE_DEADLINE_DT.setDate(SALE_DEADLINE_DT.getDate() + SALE_DEADLINE);
            				
            				context.record.set('SALE_DEADLINE_DT', SALE_DEADLINE_DT);
            			}
            			else{
            				console.log(IN_DT);
            				if(IN_DT == '' || IN_DT == null || IN_DT == undefined){
                				context.record.set('SALE_OPTION_TYPE_CD', 'A');
                				context.record.set('SALE_OPTION_TYPE_NM', '입고일로부터');
                				
                			}
                			else{
                				context.record.set('SALE_OPTION_TYPE_CD', 'B');
                				context.record.set('SALE_OPTION_TYPE_NM', '계약일로부터');
                				var SALE_DEADLINE_DT = new Date(context.record.data.CONTRACT_DT);
                				SALE_DEADLINE_DT.setDate(SALE_DEADLINE_DT.getDate() + SALE_DEADLINE);
                				
                				context.record.set('SALE_DEADLINE_DT', SALE_DEADLINE_DT);
                			}
            			}
            			
            		}
            	}
            }
        }
