    w_gridSaveBtnClick: function(button, e, eOpts) {
		var store = Ext.getStore('ColStore');
		var flag = '';
		var gridRecord = Ext.getCmp('colGrid').getSelectionModel().getSelection();
		var gridRecord1 = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		
		for(var i = 0; i < gridRecord.length; i++) {
			if(gridRecord[i].get('COL_TYPE') == '' || gridRecord[i].get('COL_TYPE') == null) {
				flag = 'F';
				msg = '담보유형을 선택하세요.';
				break; 
			} else {
				flag = 'T';
			}
		} 
		
		var makeArData = false;
		store.each(function(record, idx) {
			if(record.phantom == true) {
				record.set('V_TYPE', 'I_ADD');
			} else if (record.phantom == false) {
				record.set('V_TYPE', 'U_ADD');
			}
			
			if(!!record.get('BC_NO')) makeArData = true;
		});
			
		Ext.Ajax.request({
			url : '/HSPF01/HSPF02/mm2/po/m_col_distb/sql/M_COL_DISTB.jsp',
    		method: 'post',
    		params: {
    			V_TYPE: 'HI_ADD',  
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_MAST_DB_NO : Ext.getCmp('V_NEW_MAST_DB_NO').getValue(),
    			V_COL_DT : Ext.getCmp('V_NEW_COL_DT').getValue(),
    			V_OLD_MAST_DB_NO : Ext.getCmp('V_OLD_MAST_DB_NO').getValue(),
    			V_MVMT_NO : Ext.getCmp('W_MVMT_NO').getValue(),
    		},
    		callback : function(records,operations,success){
    			
			},
    	    success : function(response) {
    	    	if(response.responseText == 'success') {
    	    		store.sync({
        				params : {
        					V_TYPE : 'SYNC',
        					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
        					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
        					V_MAST_DB_NO: Ext.getCmp('V_NEW_MAST_DB_NO').getValue(),
        					V_COL_NO: Ext.getCmp('V_NEW_COL_NO').getValue(),
        	    			V_MVMT_NO : Ext.getCmp('W_MVMT_NO').getValue(),
        	    			V_PO_NO :  gridRecord1[0].get('PO_NO') ,//GR
        				},
        				callback : function(records, operation, success) {
        					var res = records.operations[0]._response.responseText;
        					
        					if(res.match('Exception')) {
        						Ext.Msg.alert('확인', res);
        					} else {
        						Ext.getCmp('w_tempGlCfmBtn').setDisabled(false);
        						
        						// 수금등록
        						if (makeArData){
        							Ext.Ajax.request({
            							url : '/HSPF01/HSPF02/mm2/po/m_col_distb/sql/A_AR_RECEIPT.jsp',
            							method : 'post',
            							params : {
            								V_TYPE : 'WH_COL',
            								V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
            								V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
            								V_CLS_AR_NO : Ext.getCmp('V_CLS_AR_NO').getValue(),
            								V_CLS_DT : Ext.getCmp('V_NEW_COL_DT').getValue(),
            								V_BP_CD : Ext.getCmp('W3_S_BP_CD').getValue(),
            								V_DEPT_CD : '5124',
            								V_CUR : 'KRW',
            								V_MAST_DB_NO : Ext.getCmp('V_NEW_MAST_DB_NO').getValue(),
            							},
            							success : function(response) {
            								var res = response.responseText;

            								// 정상등록
            								if (res.match('CR') || res.match('UPDATE')) {
            									if (res.match('CR')) {
            										Ext.getCmp('V_CLS_AR_NO').setValue(res);
            									}
            									
            									store.each(function(rec, idx) {
            										if (rec.isDirty()) {
            											rec.set('V_TYPE', 'I');
            										}
            									});
            									
            									store.sync({
            										params : {
            											V_TYPE : 'WD_COL',
            											V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
            											V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
            											V_CLS_AR_NO : Ext.getCmp('V_CLS_AR_NO').getValue(),
            											V_CLS_DT : Ext.getCmp('V_NEW_COL_DT').getValue(),
            											V_CUR : 'KRW',
            											V_MAST_DB_NO : Ext.getCmp('V_NEW_MAST_DB_NO').getValue(),
            										},
            										callback : function(records, operation, success) {
            											Ext.toast({
            				    							title : ' ',
            				    							timeout : 1000,
            				    							html : '저장완료',
            				    							style : 'text-align: center',
            				    							align : 't',
            				    							width : 130,
            				    						});
            										}
            									});
            								} else {
            									Ext.Msg.alert('확인', res);
            								}
            							}
            						});
        						}
        						
        						store.reload();
        					}
        				}, 
        				success: function() {
        				}
        			});
    	    	} else {
    	    		Ext.Msg.alert('확인', response.responseText);
    	    	}
    			
    	    }
		});
		
		Ext.toast({
			title : ' ',
			timeout : 1000,
			html : '저장완료',
			style : 'text-align: center',
			align : 't',
			width : 130,
		});
	},
    
