Ext.define('M_BL_TOTAL_DISTR.controller.WinBlPopController', {
	extend : 'Ext.app.Controller',
	stores : ['PopStore', 'MyStore', 'MyStore1', 'MyStore2' ],

	control : {
		"#w_blSelBtn" : {
			click : 'w_blSelBtnClick'
		},
		'#w_blRegBtn' : {
			click : 'w_blRegBtnClick'
		},
		'#popGrid' : {
			celldblclick : 'w_popGridDblClick'
		},
		"textfield[name=bl_search]" : {
			specialkey : 'tfEnter'
		}
	},

	w_blSelBtnClick : function(button, e, eOpts) {
		var popStore = this.getPopStoreStore();
		popStore.load({
			params : {
				V_TYPE : 'SP',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_LOADING_DT_FR : Ext.getCmp('W_LOADING_DT_FR').getValue(),
				W_LOADING_DT_TO : Ext.getCmp('W_LOADING_DT_TO').getValue(),
				W_BL_DOC_NO : Ext.getCmp('W_BL_DOC_NO').getValue(),
				W_LC_DOC_NO : Ext.getCmp('W_LC_DOC_NO').getValue(),
				W_PO_NO : Ext.getCmp('W_PO_NO').getValue(),
				V_M_BP_NM : Ext.getCmp('W_M_BP_NM_1').getValue(),
				V_S_BP_NM : Ext.getCmp('W_M_BP_NM2').getValue(),
			},
			callback : function(records, operations, success) {
			}
		});
	},

	w_blRegBtnClick : function(button, e, eOpts) {
		this.w_popGridDblClick();
	},

	w_popGridDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
		store.removeAll();
		store1.removeAll();
		store2.removeAll();
		
		Ext.Ajax.request({
			url : 'sql/M_BL_TOTAL_DISTR.jsp',
			method : 'post',
			params : {
				V_TYPE : 'HH',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_BL_DOC_NO : record.get('BL_DOC_NO'),
			},
			success : function(response) {
				var res = Ext.JSON.decode(response.responseText).resultList[0];

				if(res != null){
					Ext.getCmp('V_APPROV_NO').setValue(res.APPROV_NO);
					Ext.getCmp('V_APPROV_NM').setValue(res.APPROV_NM);
					Ext.getCmp('V_APPROV_DT').setValue(res.APPROV_DT);
					Ext.getCmp('V_PO_NO').setValue(res.PO_NO);
					Ext.getCmp('V_LC_DOC_NO').setValue(res.LC_DOC_NO);
					Ext.getCmp('V_OPEN_DT').setValue(res.OPEN_DT);
					Ext.getCmp('V_IN_TERMS_NM').setValue(res.IN_TERMS_NM);
					Ext.getCmp('V_PAY_METH_NM').setValue(res.PAY_METH_NM);
					Ext.getCmp('V_BL_NO').setValue(res.BL_NO);
					Ext.getCmp('V_BL_DOC_NO').setValue(res.BL_DOC_NO);
					Ext.getCmp('V_LOADING_DT').setValue(res.LOADING_DT);
					Ext.getCmp('V_XCH_RATE').setValue(res.XCH_RATE);
					Ext.getCmp('V_FORWARD_XCH').setValue(res.FORWARD_XCH);
					Ext.getCmp('V_MVMT_DT').setValue(res.MVMT_DT);
					Ext.getCmp('V_ID_DT').setValue(res.ID_DT);
				}
				
				// [첫번째 그리드] 조회
		    	store.load({
		    		params: {
		    			V_TYPE: 'D1',
		    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
		    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
		    			V_BL_DOC_NO: Ext.getCmp('V_BL_DOC_NO').getValue(),
		    		},
		    		callback: function(records,operations,success){
		    			// [두번째 그리드] 조회
		    	    	store1.load({
		    	    		params: {
		    	    			V_TYPE: 'D2',
		    	    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
		    	    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
		    	    			V_BL_DOC_NO: Ext.getCmp('V_BL_DOC_NO').getValue(),
		    	    		},
		    	    		callback: function(records,operations,success){
		    	    			// [첫번째 그리드] 조회
		    	    	    	store2.load({
		    	    	    		params: {
		    	    	    			V_TYPE: 'D3',
		    	    	    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
		    	    	    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
		    	    	    			V_BL_DOC_NO: Ext.getCmp('V_BL_DOC_NO').getValue(),
		    	    	    		},
		    	    	    		callback: function(records,operations,success){
		    	    	    			
		    	    	    		},
		    	    	    	});
		    	    		},
		    	    	});
		    		},
		    	});
				
			}
		});

		var popWin = Ext.getCmp('WinBlPop');
		popWin.destroy();
	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.w_blSelBtnClick();
		}
	}

});
