	// 입고등록버튼
	gridGrBtnClick : function(button, e, eOpts) {
    	
    	var store = Ext.getStore('MyStore');
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var flag = 'T';
		var msg = '';
		var blFlag = 'T';
		
		for(var i=0; i<gridRecord.length; i++) {
			if(gridRecord[i].get('V_TYPE') == 'V') {
				if(gridRecord[i].get('BON_QTY') == 0) {
					flag = 'F';
					msg = 'BON수량을 입력하세요.';
					break;
				} 
				else {
					flag = 'T';
					gridRecord[i].set('V_TYPE', 'I');
				}
				
				
			}
			if(gridRecord[i].get('GR_YN') == 'Y' ) {
				flag = 'F';
				msg = '이미 입고된 데이터입니다.';
				break;
			}
			if(gridRecord[i].get('BL_DOC_NO') == '' || gridRecord[i].get('BL_DOC_NO') == null || gridRecord[i].get('BL_DOC_NO') == undefined) {
//				blFlag = 'F';
				flag = 'F';
				msg = 'BL을 입력하세요.';
				break;
			}
		}
		
//		console.log('flag : ' + flag);
		
		var GR_DT = Ext.Date.format(Ext.getCmp('V_GR_DT').getValue(), 'Y-m-d');
		if(flag == 'T') {
			var ajaxFlag = '';
			Ext.Msg.confirm('확인', '입고등록하시겠습니까?<br>입고일 : ' + GR_DT, function(btn) {
				if (btn == 'yes') {
					if(blFlag == 'F'){
						Ext.Msg.confirm('확인', 'B/L번호가 없는 데이터가 존재합니다.<br>등록 하시겠습니까?', function(btn) {
							if (btn == 'yes') {
								Ext.Ajax.request({
									url : 'sql/M_GR_STEEL2.jsp',
									method : 'post',
									params : {
										V_TYPE : 'IH',
										V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
										V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
										V_GR_DT: Ext.getCmp('V_GR_DT').getValue()
									},
									success : function(response) {
										var GR_NO = response.responseText;
										
										store.sync({ 
											params : {
												V_TYPE : 'SYNC',
												V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
												V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
												V_GR_DT: Ext.getCmp('V_GR_DT').getValue(),
												V_GR_NO: GR_NO
											},
											callback : function(records, operation, success) {
												var tbController = M_GR_STEEL2.app.getController("TbButtonController");
												tbController.selBtnClick();
											}, 
											success: function() {
												
											}
										});
										
									}
								});
							}
						});
					}
					else{
						Ext.Ajax.request({
							url : 'sql/M_GR_STEEL2.jsp',
							method : 'post',
							params : {
								V_TYPE : 'IH',
								V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
								V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
								V_GR_DT: Ext.getCmp('V_GR_DT').getValue()
							},
							success : function(response) {
								var GR_NO = response.responseText;
								
								store.sync({ 
									params : {
										V_TYPE : 'SYNC',
										V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
										V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
										V_GR_DT: Ext.getCmp('V_GR_DT').getValue(),
										V_GR_NO: GR_NO
									},
									callback : function(records, operation, success) {
										var tbController = M_GR_STEEL2.app.getController("TbButtonController");
										tbController.selBtnClick();
									}, 
									success: function() {
										
									}
								});
								
							}
						});
					}
				}
			})
		} else {
			Ext.Msg.alert('확인', msg);
		}
		
    	
    },
