Ext.define('M_GR_STEEL2.controller.MyGridController2', {
    extend: 'Ext.app.Controller',
    control: {
        "#gridAddBtn2": {
            click: 'gridAddBtn2Click'
        },
        "#gridDelBtn2": {
            click: 'gridDelBtn2Click'
        },
        "#gridSaveBtn2": {
        	click: 'gridSaveBtn2Click'
        },
        "#xlsxDown2": {
            click: 'xlsxDown2Click'
        },
    },
    gridAddBtn2Click: function(button, e, eOpts) {
    	var gridRecord = Ext.getCmp('myGrid1').getSelectionModel().getSelection();
    	if(gridRecord.length == 1) {
    		 var popup = Ext.create("M_GR_STEEL2.view.WinAddRow");
    			popup.show();
    			Ext.getCmp('rowCount').setValue(1);
    	} else {
    		Ext.Msg.alert('확인', '품목정보를 추가할 입고를 좌측에서 1건 선택하세요.');
    	}
    },
gridDelBtn2Click: function(button, e, eOpts) {
		
		var store = Ext.getStore('MyStore');
		var store1 = Ext.getStore('MyStore1');
		var store2 = Ext.getStore('MyStore2');
		var flag = '';
		var msg = '';
		
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var gridRecord1 = Ext.getCmp('myGrid1').getSelectionModel().getSelection();

		
		Ext.Msg.confirm('확인', '삭제하시겠습니까?', function(button) {
			if (button == 'yes') {
				
				store2.each(function(rec, idx) {
					if(rec.get('V_TYPE') == 'V') {
						rec.set('V_TYPE', 'D');
					}
				})
				store2.sync({
					params : {
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					},
					callback : function(records, operation, success) {
						store1.reload();
						store2.reload();
						
					}, 
					success: function() {
					}
				});
			}
		});
	},
    gridSaveBtn2Click: function(button, e, eOpts) {
    	var store1= Ext.getStore('MyStore1');
    	var store2= Ext.getStore('MyStore2');
    	store2.each( function(rec,idx) {
    		if(rec.phantom == true) {
    			rec.set('V_TYPE', 'I');
    		} else {
    			rec.set('V_TYPE', 'U');
    		}
    	});
    	
    	store2.sync({ 
			params : {
				V_TYPE : 'SYNC',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
			},
			callback : function(records, operation, success) {
				store1.reload();
				store2.reload()
			}, 
			success: function() {
				
			}
		});
    },
    xlsxDown2Click: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid2');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: '국내상품입고등록(상세)', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    }
});
