Ext.define('M_IV_STEEL.controller.PopController', {
    extend: 'Ext.app.Controller',
    stores : [ 'MyStore1', 'PopStore'],
    
    control: {
		"#w_selBtn1" : {
			click : 'w_selBtn1Click'
		},
		'#popGrid': {
			celldblclick: 'w_popGridDblClick'
		},
		"textfield[name=pop_search]": {
            specialkey: 'tfEnter'
        }
	},

	w_selBtn1Click: function(button, e, eOpts) {
		var popStore = this.getPopStoreStore();
		popStore.load({
    		params: {
    			V_TYPE: 'PS',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_IV_DT_FR : Ext.getCmp('W_IV_DT_FR').getValue(),
				W_IV_DT_TO : Ext.getCmp('W_IV_DT_TO').getValue(),
				W_M_BP_NM : Ext.getCmp('W_M_BP_NM2').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});
	},
	
	w_popGridDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		Ext.getCmp('V_IV_NO').setValue(record.get('IV_NO'));

    	var store1 = this.getMyStore1Store();
    	store1.removeAll();
    	
    	Ext.Ajax.request({
			url : 'sql/M_IV_STEEL.jsp',
			method : 'post',
			params: {
				V_TYPE: 'SH',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_IV_NO : record.get('IV_NO'),
			},
			success: function(response) {
				var res = Ext.JSON.decode(response.responseText).resultList[0];
				
				Ext.getCmp('V_IV_DT').setValue(res.IV_DT);
    			Ext.getCmp('V_IV_TYPE').setValue(res.IV_TYPE);
    			Ext.getCmp('V_M_BP_CD2').setValue(res.M_BP_CD);
    			Ext.getCmp('V_M_BP_NM2').setValue(res.M_BP_NM);
    			Ext.getCmp('V_T_BP_CD').setValue(res.T_BP_CD);
    			Ext.getCmp('V_T_BP_NM').setValue(res.T_BP_NM);
				Ext.getCmp('V_IV_TYPE').setValue(res.IV_TYPE);
				Ext.getCmp('V_TAX_CD').setValue(res.TAX_CD);
				Ext.getCmp('V_CUR').setValue(res.CUR);
				Ext.getCmp('V_IN_TERMS').setValue(res.IN_TERMS);
				Ext.getCmp('V_PAY_METH').setValue(res.PAY_METH);
				Ext.getCmp('V_BL_DOC_NO').setValue(res.BL_DOC_NO);
				Ext.getCmp('V_XCHG_RT').setValue(res.XCHG_RT);
				Ext.getCmp('V_ELEC_VAT_YN').setValue(res.ELEC_VAT_YN);
				Ext.getCmp('V_IV_ISSUE_DT').setValue(res.IV_ISSUE_DT);
				Ext.getCmp('V_TEMP_GL_NO').setValue(res.TEMP_GL_NO);
				
				/*전표유무*/
    			if(res.TEMP_GL_NO== undefined) {
    				Ext.getCmp('ivRegBtn').setDisabled(false);
    				Ext.getCmp('gridDelBtn').setDisabled(false);
    				Ext.getCmp('tempGlCfmBtn').setDisabled(false);
    				Ext.getCmp('tempGlCancelBtn').setDisabled(true);
    			} else {
    				Ext.getCmp('ivRegBtn').setDisabled(true);
    				Ext.getCmp('gridDelBtn').setDisabled(true);
    				Ext.getCmp('tempGlCfmBtn').setDisabled(true);
    				Ext.getCmp('tempGlCancelBtn').setDisabled(false);
    			}
//    			
    			if(res.ELEC_VAT_YN == 'Y') {
    				Ext.getCmp('rb1Y').setValue(true);
    			} else {
    				Ext.getCmp('rb1N').setValue(true);
    			}

    			store1.load({
		    		params: {
		    			V_TYPE: 'SD',
		    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_IV_NO : record.get('IV_NO'),
		    		},
		    		callback: function(records,operations,success){
		    			
//		    			var DOC_AMT = 0;
//		    			var LOC_AMT = 0;
//		    			var XCH_RATE =  Ext.getCmp('V_XCH_RATE').getValue(); 
//		    			var QTY = 0;
		    		}
				});
		
				var popWin=  Ext.getCmp('WinIvPop');
				popWin.destroy();
			}
    	});
				

    			
				
	},
	
	tfEnter: function(field, e, eOpts) {
    	if (e.getKey() == e.ENTER) {
    		this.w_selBtn1Click();
    	}
}
	

});
