Ext.define('Y_APPROV_STEEL.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
    	var count = store.getCount();
    	var rowCount = Number(Ext.getCmp('rowCount').getValue());

    	for(var i=0; i<rowCount; i++) {
    		count = count + 1;
    		
    		if(Ext.getCmp('V_IV_TYPE').getValue() == 'L') { //내수
    			var rec = Ext.create('Y_APPROV_STEEL.model.MyModel', {
        			ST_TYPE : 'F',
        			CUR: 'KRW',
        			TAX_RT: 0,
        			PO_QTY: 0,
        			PO_AMT: 0,
        			PO_PRC: 0,
        			LC_RT: 0,
        			REC_RT: 0,
        			USANCE_RT: 0,
        			INSUR_RT: 0,
        			COMMISSION_RT: 0,
        			APPROV_NO: Ext.getCmp('V_APPROV_NO').getValue(),
        			APPROV_NM: Ext.getCmp('V_APPROV_NM').getValue(),
        			S_BP_CD: Ext.getCmp('V_S_BP_CD').getValue(),
        			S_BP_NM: Ext.getCmp('V_S_BP_NM').getValue(),
        			APPROV_DT: Ext.getCmp('V_APPROV_DT').getValue(),
        			XCH_RATE : 1,
        			USANCE_CLS : 'B',
        			REC_CLS : 'A',
        		});
    		} else {
    			var rec = Ext.create('Y_APPROV_STEEL.model.MyModel', {
        			ST_TYPE : 'F',
        			CUR: 'USD',
        			USANCE_TYPE: 'L9',
        			TAX_RT: 0,
        			PO_QTY: 0,
        			PO_AMT: 0,
        			PO_PRC: 0,
        			LC_RT: 0,
        			REC_RT: 0,
        			USANCE_RT: 0,
        			INSUR_RT: 0,
        			COMMISSION_RT: 0,
        			APPROV_NO: Ext.getCmp('V_APPROV_NO').getValue(),
        			APPROV_NM: Ext.getCmp('V_APPROV_NM').getValue(),
        			S_BP_CD: Ext.getCmp('V_S_BP_CD').getValue(),
        			S_BP_NM: Ext.getCmp('V_S_BP_NM').getValue(),
        			APPROV_DT: Ext.getCmp('V_APPROV_DT').getValue(),
        			XCH_RATE: Ext.getCmp('V_USD_RATE').getValue(),
        			USANCE_CLS : 'B',
        			REC_CLS : 'A',
        		});
    		}
    		
    		store.insert(count-1, rec);
    	}

    	var win = Ext.getCmp('WinAddRow');
    	win.destroy();

    }

});
