Ext.define('Z_WBS_MAST.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: [ 'MyStore' ],
    
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "myform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	store.removeAll();
    	
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_DEV_PLAN_DT_FR: Ext.getCmp('V_DEV_PLAN_DT_FR').getValue(),
    			V_DEV_PLAN_DT_TO: Ext.getCmp('V_DEV_PLAN_DT_TO').getValue(),
    			V_DEV_END_DT_FR: Ext.getCmp('V_DEV_END_DT_FR').getValue(),
    			V_DEV_END_DT_TO: Ext.getCmp('V_DEV_END_DT_TO').getValue(),
    			V_SYS_TYPE: Ext.getCmp('V_SYS_TYPE').getValue(),
    			V_PGM_NM: Ext.getCmp('V_PGM_NM').getValue(),
    			V_DEV_USR_NM: Ext.getCmp('V_DEV_USR_NM').getValue(),
    			V_COMPLETE_YN: Ext.getCmp('V_COMPLETE_YN').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});

    },

    saveBtnClick: function(button, e, eOpts) {
    	var store = Ext.getStore('MyStore');
    	var flag = 'T';
    	var msg = '';
    	store.each(function(record, idx) {
    		if(record.get('SYS_TYPE') == null)
    		{
    				flag = 'F';
    				msg = '시스템명을 입력하세요.'
    		}
			if (record.get('V_TYPE') != 'I' && record.dirty == true) {
				record.set('V_TYPE', 'U');
			}
		});
    	
    	if (flag == 'T'){
    		Ext.Msg.confirm('확인', '저장하시겠습니까?', function(button) {
    			if (button == 'yes') {
    				store.sync({
    					params : {
    						V_TYPE : 'SYNC',
    						V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    		    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    		    			/*V_SYS_TYPE: Ext.getCmp('V_SYS_TYPE').getValue(),
    		    			V_PGM_NM: Ext.getCmp('V_PGM_NM').getValue(),
    		    			V_DEV_USR_NM: Ext.getCmp('V_DEV_USR_NM').getValue(),
    		    			V_COMPLETE_YN: Ext.getCmp('V_COMPLETE_YN').getValue(),*/
    					},
    					callback : function(records, operation, success) {
    						var tbController = Z_WBS_MAST.app.getController("TbButtonController");
    						tbController.selBtnClick();
    						
    						Ext.toast({
    							title : ' ',
    							timeout : 1000,
    							html : '저장완료',
    							style : 'text-align: center',
    							align : 't',
    							width : 130,
    						});
    					},
    				});
    			}
        	});
    	} else {
    		Ext.Msg.alert('확인', msg);
    	}
    	
    	
    },

    clrBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	store.removeAll();
    	Ext.getCmp('V_SYS_TYPE').setValue('T');
    	Ext.getCmp('V_PGM_NM').setValue('');
    	Ext.getCmp('V_DEV_USR_NM').setValue('');
    	Ext.getCmp('V_COMPLETE_YN').setValue('T');
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

    tfEnter: function(field, e, eOpts) {
        if (e.getKey() == e.ENTER) {
        	this.selBtnClick();
        }
    },  
    
    onLaunch: function(application) {
    	var BPstore1 = Ext.getStore('IT01Store');
        BPstore1.load();
        
        var deptCd = parent.Ext.getCmp('GDEPT_CD').getValue();
		if("/2417".indexOf(deptCd) < 0){
	    	Ext.getCmp('selBtn').setDisabled(true);
	    	Ext.getCmp('saveBtn').setDisabled(true);
	    	Ext.getCmp('gridAddBtn').setHidden(true);
	    	Ext.getCmp('gridDelBtn').setHidden(true);
		}
    },
    
});
