Ext.define('M_COL_ARR_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: ['MyStore', 'MyStore1'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "myviewport textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var activeTab = Ext.getCmp('myTab').getActiveTab().xtype;
    	
    	if(activeTab === 'mypanel'){
    		store.removeAll();
    		
    		// [첫번째 탭] 조회
        	store.load({
        		params: {
        			V_TYPE: 'T1S',
        			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
        			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
        		},
        		callback: function(records,operations,success){
        			
        		},
        	});
    	} else if(activeTab === 'mypanel1'){
    		store1.removeAll();
    		
    		// [두번째 탭] 조회
        	store1.load({
        		params: {
        			V_TYPE: 'T2S',
        			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
        			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
        		},
        		callback: function(records,operations,success){
        			
        		},
        	});
    	}
		
    },

    saveBtnClick: function(button, e, eOpts) {
    	
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},
	
	clrBtnClick: function(button, e, eOpts) {
		Ext.getCmp('V_LC_DOC_NO').setValue('');
    	Ext.getCmp('V_BL_DOC_NO').setValue('');
	},

    tfEnter: function(field, e, eOpts) {
        if (e.getKey() == e.ENTER) {
        	this.selBtnClick();
        }
    },

});
