Ext.define('M_COL_CHANGE_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: ['MyStore', 'MyStore1', 'AcctStore', 'BankStore', 'BankAcctStore'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "myform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	store.removeAll();
    	store1.removeAll();
    	
    	store.load({
    		params: {
    			V_TYPE: 'SH',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_DEPT_CD: Ext.getCmp('V_DEPT_CD').getValue(),
    			V_MAST_DB_NO: Ext.getCmp('V_MAST_DB_NO').getValue(),
    			V_BP_CD: Ext.getCmp('V_BP_CD').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});

    },

    saveBtnClick: function(button, e, eOpts) {
    	var store = Ext.getStore('MyStore');
    	
    	store.each(function(record, idx) {
			if(record.isDirty()) {
				record.set('V_TYPE', 'U');
			}
		});
    	
    	store.sync({
			params : {
				V_TYPE : 'SYNC',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_YYYYMM: Ext.getCmp('V_YYYYMM').getValue(),
			},
			callback : function(records, operation, success) {
				var res = records.operations[0]._response.responseText;

				if (res.match('Exception')) {
					Ext.Msg.alert('확인', res);
				} else {
					store.reload();
					Ext.toast({
						title : ' ',
						timeout : 1000,
						html : '저장완료',
						style : 'text-align: center',
						align : 't',
						width : 130,
					});
				}
				
			},
			success : function(response) {
			}
		});
    },

    clrBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	store.removeAll();
    	store1.removeAll();
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

    tfEnter: function(field, e, eOpts) {
        if (e.getKey() == e.ENTER) {
        	this.selBtnClick();
        }
    },  
    
    onLaunch: function(application) {
    	var BPstore1 = Ext.getStore('WinBpPopStore');
        BPstore1.load();
        
      var AcctStore = Ext.getStore('AcctStore');
      AcctStore.load();

		var BankStore = Ext.getStore('BankStore');
		BankStore.load();
		
		var BankAcctStore = Ext.getStore('BankAcctStore');
		BankAcctStore.load();
    },
    
});
