Ext.define('M_FORWARD_EXCHANGE_REQ_HSPF.controller.MyGridController', {
    extend: 'Ext.app.Controller',	
    stores: ['MyStore', 'MyStore1', 'PopStore'],
    control: {
        "#gridAddBtn": {
            click: 'gridAddBtnClick'
        },
        "#gridDelBtn": {
            click: 'gridDelBtnClick'
        },
        "#xlsxDown": {
            click: 'xlsxDownClick'
        },
    },


    gridAddBtnClick: function(button, e, eOpts) {
    	var window = Ext.create('M_FORWARD_EXCHANGE_REQ_HSPF.view.WinAddRow');
    	
    	var store = Ext.getStore('PopStore');
    	store.removeAll();
    	
    	window.show();
    },
    xlsxDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: '출고등록(상단)', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    },
    
    gridDelBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var record = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	
    	var controller = this.getController('TbButtonController');
    	
    	if(record.length < 1){
    		Ext.Msg.alert('확인', '행을 선택해주세요');
    	}
    	else{
    		Ext.MessageBox.confirm('확인', '삭제 하시겠습니까?', function(btn) {
				if (btn == 'yes') {
					var SYNC_FLAG = 'F';
					for(var i = 0 ; i < record.length ; i ++){
		        		if(record[i].phantom == true){
		        			store.remove(record[i]);
		        		}
		        		else{
		        			record[i].set('V_TYPE', 'D');
		        			SYNC_FLAG = 'T';
		        		}
		        	}
					if(SYNC_FLAG == 'T'){
						store.sync({
	    	        		params:{
	    	        			V_TYPE : 'SYNC',
	    	        			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
	    	        			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
	    	        		},
	    	        		callback: function(records,operations,success){
	    	        			controller.selBtnClick();
	    	        		}
	    	        	});
					}
				}
    		});
    	}
    },
    
});
