Ext.define('item_register.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
            	var count = store.getCount();
                //var rowCount = store.getCount();
            	var rowCount = Number(Ext.getCmp('rowCount').getValue());

            	for(var i=0; i<rowCount; i++) {
            		count = count + 1;
            		var rec = Ext.create('item_register.model.MyModel', {
            			M_PRICE : 1,
            			S_PRICE : 1,
            		});
                     	rec.set('CFM_YN', 'N');
                    	rec.set('INSROW', 'Y');
            		store.insert(0, rec);
            	}

            	var win = Ext.WindowManager.getActive();
            	if(win){
            	     win.close();
            	}
    }

});
