Ext.define('inoutstatus_supp.controller.MyController', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore'
    ],

    onLaunch: function(application) {
        	var now = new Date();
        		var firstDate = new Date(now.getFullYear(),now.getMonth()-1);
                var lastDate_dlvy = new Date(now.getFullYear(),now.getMonth()+4,0);
                var lastDate = new Date(now.getFullYear(),now.getMonth()+1,0);
                Ext.getCmp('u_dlvy_dt_from').setValue(firstDate);
                Ext.getCmp('u_dlvy_dt_to').setValue(lastDate_dlvy);
                Ext.getCmp('u_gr_dt_from').setValue(firstDate);
                Ext.getCmp('u_gr_dt_to').setValue(lastDate);
                Ext.getCmp('u_dn_dt_from').setValue(firstDate);
                Ext.getCmp('u_dn_dt_to').setValue(lastDate);
    }

});
