Ext.define('inoutstatus_supp.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore'
    ],

    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
        //alert('select');
        	var store=this.getMyStoreStore();

                store.load({
                	params: {
                		u_pono: Ext.getCmp('u_pono').getValue(), // 납기요청일
                		u_dlvy_dt_from: Ext.getCmp('u_dlvy_dt_from').getValue(), // 발주일자
                		u_dlvy_dt_to: Ext.getCmp('u_dlvy_dt_to').getValue(), // 발주일자
                		u_itemcd: Ext.getCmp('u_itemcd').getValue(), // 발주번호
                		u_gr_dt_from: Ext.getCmp('u_gr_dt_from').getValue(),
                		u_gr_dt_to: Ext.getCmp('u_gr_dt_to').getValue(),
        //        		u_contno: parent.Ext.getCmp('u_contno').getValue(),
        //        		u_invno: parent.Ext.getCmp('u_invno').getValue(),
                		u_dn_dt_from: Ext.getCmp('u_dn_dt_from').getValue(),
                		u_dn_dt_to: Ext.getCmp('u_dn_dt_to').getValue(),
                		u_bp_cd : parent.Ext.getCmp('GBP_CD').getValue()
                	},
                    callback: function (records, operation, success){
                   },
                   success: function() {
                   },
                   scope: this
                })
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
        var tab=parent.Ext.getCmp('myTab');
        var activeTab=tab.getActiveTab();
        var tabIndex=tab.items.indexOf(activeTab);
        tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
