Ext.define('B_BIZ_PAY_INFO_HSPF.controller.MyGridController', {
	extend : 'Ext.app.Controller',
	stores : [ 'MyStore' ],

	control : {
		"#gridAddBtn" : {
			click : 'gridAddBtnClick'
		},
		"#gridDelBtn" : {
			click : 'gridDelBtnClick'
		},
		"#xlsxDown" : {
			click : 'xlsxDownClick'
		},
	},

	gridAddBtnClick : function(button, e, eOpts) {
		var popup = Ext.create("B_BIZ_PAY_INFO_HSPF.view.WinAddRow");
		popup.show();
		Ext.getCmp('rowCount').setValue(1);
	},

	gridDelBtnClick : function(button, e, eOpts) {
		var myStore = Ext.getStore('MyStore');
		var myGrid = Ext.getCmp('myGrid').getSelection();
		var flag = 'F';
		
		for(var i=0; i<myGrid.length; i++) {
			if(myGrid[i].get('V_TYPE') == 'I') {
				myStore.remove(myGrid[i]);
			} else {
				myGrid[i].set('V_TYPE', 'D');
				flag = 'T';
			}
		}
		
		if (flag == 'T') {
			Ext.Msg.confirm('확인', '삭제하시겠습니까?', function(button) {
				if (button == 'yes') {
					myStore.sync({
						params : {
							V_TYPE : 'SYNC',
							V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						},
						callback : function(records, operation, success) {
							var tbController = B_BIZ_PAY_INFO_HSPF.app.getController("TbButtonController");
							tbController.selBtnClick();
							
							Ext.toast({
								title : ' ',
								timeout : 1000,
								html : '삭제완료',
								style : 'text-align: center',
								align : 't',
								width : 130,
							});
						},
					});
				}
			});
		}
		
	},

	xlsxDownClick : function(button, e, eOpts) {
		var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
		var grid = Ext.getCmp('myGrid');
		grid.saveDocumentAs({
			type : 'xlsx',
			title : '거래처별 결제조건기준등록', // 엑셀내타이틀
			fileName : currentDate + '.xlsx' // 엑셀파일이름
		});
	},

});
