Ext.define('B_CALENDAR_TOT_HSPF.controller.MyGridController', {
	extend : 'Ext.app.Controller',

	control : {
		"#gridExecBtn" : {
			click : 'gridExecBtnClick'
		},
		"#xlsxDown" : {
			click : 'xlsxDownClick'
		},
	},
	
	gridExecBtnClick : function(button, e, eOpts) {
		// 1영업일 전까지만 마감할 수 있도록 제한
		Ext.Ajax.request({
			url : 'sql/B_CALENDAR_TOT_HSPF.jsp',
			method : 'post',
			params : {
				V_TYPE : 'C',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_YYYY : Ext.getCmp('V_YYYY').getValue(),
			},
			success : function(response) {
				Ext.toast({
					title : ' ',
					timeout : 1000,
					html : '생성완료',
					style : 'text-align: center',
					align : 't',
					width : 130,
				});
				
				var tbController = B_CALENDAR_TOT_HSPF.app.getController("TbButtonController");
	    		tbController.selBtnClick();
			}
		})
	},

	xlsxDownClick : function(button, e, eOpts) {
		var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
		var grid = Ext.getCmp('myGrid');
		grid.saveDocumentAs({
			type : 'xlsx',
			title : '공휴일등록', // 엑셀내타이틀
			fileName : currentDate + '.xlsx' // 엑셀파일이름
		});
	},

});
