Ext.define('S_BL_STATE_TOT_HSPF.controller.MyGridController', {
    extend: 'Ext.app.Controller',

    stores: [ 'MyStore' ],

    control: {
        "#gridAddBtn": {
            click: 'gridAddBtnClick'
        },
        "#gridDelBtn": {
            click: 'gridDelBtnClick'
        },
        "#xlsxDown": {
            click: 'xlsxDownClick'
        },
        "#printInvoiceBtn": {
            click: 'printInvoiceBtnClick'
        },
        "#printPackingBtn": {
            click: 'printPackingBtnClick'
        },
    },

    gridAddBtnClick: function(button, e, eOpts) {
        var popup = Ext.create("S_BL_STATE_TOT_HSPF.view.WinAddRow");
        popup.show();
        Ext.getCmp('rowCount').setValue(1);
    },

    gridDelBtnClick: function(button, e, eOpts) {},

    xlsxDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: 'BL현황조회', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    },

    printInvoiceBtnClick: function(button, e, eOpts) {
    	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	
    	if(gridRecord.length < 1){
    		Ext.Msg.alert('확인', '선택된 행이 없습니다.');
    		return;
    	} else if(gridRecord.length > 1){
    		Ext.Msg.alert('확인', '한 건씩 출력 가능합니다.');
    		return;
    	} else if(!!!gridRecord[0].get('BL_NO')){
    		Ext.Msg.alert('확인', 'BL번호가 존재하지 않는 행이 선택되었습니다.');
    		return;
    	} else {
    		var V_BL_NO= gridRecord[0].get('BL_NO');
    		
    		if(parent.Ext.getCmp('MAIN_SERVER_YN').getValue() == 'Y'){
		    	url = 'http://123.142.124.170:8080/aireport/on_server/S_BL_INVOICE_TOT_HSPF.jsp?BL_NO=' + V_BL_NO;  		
			} else{
				url = 'http://123.142.124.170:8080/aireport/on_server/S_BL_INVOICE_TOT_HSPF_TEST.jsp?BL_NO=' + V_BL_NO;  	 	
			}
	    	
			Ext.Msg.confirm('확인', 'INVOICE를 출력하시겠습니까?', function(button) {
        		if (button == 'yes') {
					var myWin = new Ext.Window({
						title : 'COMMERCIAL INVOICE',
						html : '<iframe name="xxx" border =0 src="'+url+'" width="100%" height="100%"></iframe>',
						width : 1000,
						height : 768,
						modal : true
					});
					
					myWin.show();
					myWin.setSize(Ext.getBody().getViewSize());
					myWin.setPagePosition(0, 0);
        		}
        	});
    	}
    },

    printPackingBtnClick: function(button, e, eOpts) {
    	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	
    	if(gridRecord.length < 1){
    		Ext.Msg.alert('확인', '선택된 행이 없습니다.');
    		return;
    	} else if(gridRecord.length > 1){
    		Ext.Msg.alert('확인', '한 건씩 출력 가능합니다.');
    		return;
    	} else if(!!!gridRecord[0].get('BL_NO')){
    		Ext.Msg.alert('확인', 'BL번호가 존재하지 않는 행이 선택되었습니다.');
    		return;
    	} else {
    		var V_BL_NO= gridRecord[0].get('BL_NO');
    		
    		if(parent.Ext.getCmp('MAIN_SERVER_YN').getValue() == 'Y'){
		    	url = 'http://123.142.124.170:8080/aireport/on_server/S_BL_PACKING_LIST_TOT_HSPF.jsp?BL_NO=' + V_BL_NO;  	
			} else{
				url = 'http://123.142.124.170:8080/aireport/on_server/S_BL_PACKING_LIST_TOT_HSPF_TEST.jsp?BL_NO=' + V_BL_NO;  		  	
			}
	    	
			Ext.Msg.confirm('확인', 'PACKING LIST를 출력하시겠습니까?', function(button) {
        		if (button == 'yes') {
					var myWin = new Ext.Window({
						title : 'PACKING LIST',
						html : '<iframe name="xxx" border =0 src="'+url+'" width="100%" height="100%"></iframe>',
						width : 1000,
						height : 768,
						modal : true
					});
					
					myWin.show();
					myWin.setSize(Ext.getBody().getViewSize());
					myWin.setPagePosition(0, 0);
        		}
        	});
    	}
    },

});
