    	getRowClass: function(record, index) {
            var STOCK_QTY = record.get('STOCK_QTY'); 
            var REQ_QTY = record.get('REQ_QTY'); 
            if(STOCK_QTY == 0) {
            	return 'bg-green';
            }
            if(REQ_QTY > 0){
            	return 'bg-dn-yellow';
            }
        },
