Ext.define('M_LC_AMEND_TOT_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore', 'MyStore1', 'MyStore2'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "myform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
    	var activeTab = Ext.getCmp('myTab').getActiveTab().xtype;
    	
//		store1.removeAll();
		
		if(activeTab === 'mypanel'){
    		store.removeAll();
    		
    		// [첫번째 탭] 조회
        	store.load({
        		params: {
        			V_TYPE: 'T1S',
        			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
        			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
        			V_LC_DT_FR : Ext.getCmp('V_LC_DT_FR').getValue(),
        			V_LC_DT_TO : Ext.getCmp('V_LC_DT_TO').getValue(),
        			V_M_BP_NM : Ext.getCmp('V_M_BP_NM').getValue(),
        		},
        		callback: function(records,operations,success){
        			
        		},
        	});
    	} else if(activeTab === 'mypanel1'){
    		store2.removeAll();
    		
    		// [두번째 탭] 조회
        	store2.load({
        		params: {
        			V_TYPE: 'T2S',
        			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
        			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
        			V_LC_DT_FR : Ext.getCmp('V_LC_DT_FR').getValue(),
        			V_LC_DT_TO : Ext.getCmp('V_LC_DT_TO').getValue(),
        			V_M_BP_NM : Ext.getCmp('V_M_BP_NM').getValue(),
        		},
        		callback: function(records,operations,success){
        			
        		},
        	});
    	}
    	
    	//[하단]발주참조조회
    	if((Ext.getCmp('V_LC_AMD_NO').getValue() != '')) {
    		// LC디테일 조회
        	Ext.Ajax.request({
    			url : 'sql/M_LC_AMEND_TOT_HSPF.jsp',
    			method : 'post',
    			params: {
    				V_TYPE: 'SH',
        			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    				V_LC_AMD_NO : Ext.getCmp('V_LC_AMD_NO').getValue(),
    			},
    			success: function(response) {
    				var res = Ext.JSON.decode(response.responseText).resultList[0];
    				
    		    	Ext.getCmp('V_OPEN_DT').setValue(res.OPEN_DT);
    		    	Ext.getCmp('V_AMEND_DT').setValue(res.AMEND_DT);
    		    	Ext.getCmp('V_M_BP_NM2').setValue(res.M_BP_NM);
    		    	Ext.getCmp('V_M_BP_CD2').setValue(res.M_BP_CD);
    		    	Ext.getCmp('V_LC_DOC_NO').setValue(res.LC_DOC_NO);
    		    	Ext.getCmp('V_LC_NO').setValue(res.LC_NO);
    		    	Ext.getCmp('V_LC_AMEND_SEQ').setValue(res.LC_AMEND_SEQ);
    		    	Ext.getCmp('V_BANK_CD').setValue(res.BANK_CD);
    		    	Ext.getCmp('V_CUR').setValue(res.CUR);
    		    	Ext.getCmp('V_XCH_RATE').setValue(res.XCH_RATE);
    		    	Ext.getCmp('V_LC_KIND').setValue(res.LC_KIND);
    		    	
    				//LC디테일 조회
    				store1.load({
    		    		params: {
    		    			V_TYPE: 'SD',
    		    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    						V_LC_AMD_NO : Ext.getCmp('V_LC_AMD_NO').getValue(),
    		    		},
    		    		callback: function(records,operations,success){
    		    		}
    		    	});
    			}
        	});
    	}
    },

    saveBtnClick: function(button, e, eOpts) {
        alert('save');
    },

    clrBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
    	
    	store.removeAll();
    	store1.removeAll();
    	store2.removeAll();

    	var nows = new Date();
		Ext.getCmp('V_OPEN_DT').setValue(nows);
		Ext.getCmp('V_AMEND_DT').setValue(nows);
		Ext.getCmp('V_M_BP_NM2').setValue('');
		Ext.getCmp('V_M_BP_CD2').setValue('');
		Ext.getCmp('V_LC_AMD_NO').setValue('');
		Ext.getCmp('V_LC_AMEND_SEQ').setValue('');
		Ext.getCmp('V_LC_DOC_NO').setValue('');
		Ext.getCmp('V_BANK_CD').setValue(null);
		Ext.getCmp('V_CUR').setValue('USD');
		Ext.getCmp('V_XCH_RATE').setValue(1);		
    	Ext.getCmp('V_M_BP_NM').setValue('');
    	Ext.getCmp('V_LC_KIND').setValue('');
    	
    	Ext.getCmp('V_LC_NO').setValue('');
    	Ext.getCmp('V_PO_NO').setValue('');
		Ext.getCmp('V_PAY_METH').setValue('');
		Ext.getCmp('V_IN_TERMS').setValue('');
		Ext.getCmp('V_DOC_AMT').setValue('');
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    },
    
    onlaunch: function() {
    	Ext.Ajax.request({
			url : '/HSPF01/common/sql/ERP_DB.jsp',
			method : 'post',
			params: {
				V_TYPE: 'XCH_RT',
				V_DATE : Ext.getCmp('V_OPEN_DT').getValue(),
				V_CUR : Ext.getCmp('V_CUR').getValue(),
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
			},
			success: function(response) {
				var res = response.responseText;
				if(res == 'NODATA') {
//					Ext.Msg.alert('확인', '해당 날짜 환율정보가 없습니다. 관리자에게 문의하거나 임의로 입력하세요.');
				} else {
					Ext.getCmp('V_XCH_RATE').setValue(res);
				}
			}
		});
    }

});
