Ext.define('M_MPM_CHK_DAY_TOT_HSPF.controller.MyGridController', {
	extend : 'Ext.app.Controller',

	control : {
		"#gridAddBtn" : {
			click : 'gridAddBtnClick'
		},
		"#gridDelBtn" : {
			click : 'gridDelBtnClick'
		},
		"#gridSaveBtn" : {
			click : 'gridSaveBtnClick'
		},
		"#gridFinishBtn" : {
			click : 'gridFinishBtnClick'
		},
		"#xlsxDown" : {
			click : 'xlsxDownClick'
		},
	},

	gridAddBtnClick : function(button, e, eOpts) {},

	gridDelBtnClick : function(button, e, eOpts) {},

	gridSaveBtnClick : function(button, e, eOpts) {},
	
	gridFinishBtnClick : function(button, e, eOpts) {
		var msg = '기간: ' + Ext.getCmp('V_DT_FR').lastValue + ' ~ ' + Ext.getCmp('V_DT_TO').lastValue + ' <br><br>마감 하시겠습니까?';
		var date = Ext.getCmp('V_DT_FR').getValue();
		if(date.getFullYear() < 2020 || (date.getFullYear() <= 2020 && date.getMonth()+1 < 6)){
			Ext.Msg.alert('확인', '2020년 6월부터 마감 가능합니다.');
			return;
		}

		Ext.Msg.confirm('확인', msg, function(button) {
			if (button == 'yes') {
				var myMask = new Ext.LoadMask(Ext.getCmp('myGrid') , {msg:"Please wait..."});
				myMask.show();
				
				Ext.Ajax.request({
					url : 'sql/M_MPM_CHK_DAY_TOT_HSPF.jsp',
					method : 'post',
					timeout: 180000,
					params : {
						V_TYPE : 'FNS',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_DT_FR: Ext.getCmp('V_DT_FR').getValue(),
						V_DT_TO: Ext.getCmp('V_DT_TO').getValue(),
					},
					callback: function() {
						myMask.hide();
					},
					success : function(response) {
						myMask.hide();
						
						Ext.toast({
							title : ' ',
							timeout : 1000,
							html : '완료',
							style : 'text-align: center',
							align : 't',
							width : 130,
						});
					}
				})
			}
		});
	},
	
	xlsxDownClick : function(button, e, eOpts) {
		var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
		var grid = Ext.getCmp('myGrid');
		grid.saveDocumentAs({
			type : 'xlsx',
			title : '일별입출고현황', // 엑셀내타이틀
			fileName : currentDate + '.xlsx' // 엑셀파일이름
		});
	},

});
