Ext.define('I_INV_SUM_TOT_HSPF.controller.MyGridController', {
	extend : 'Ext.app.Controller',

	control : {
		"#gridExecBtn" : {
			click : 'gridExecBtnClick'
		},
		"#xlsxDown" : {
			click : 'xlsxDownClick'
		},
	},
	
	gridExecBtnClick : function(button, e, eOpts) {
		let execDate = Ext.getCmp('V_YYYYMM').getValue();
		if(execDate.getFullYear() < 2020 || (execDate.getFullYear() <= 2020 && execDate.getMonth()+1 < 5)){
			Ext.Msg.alert('확인', '2020년 5월부터 실행 가능합니다.');
			return;
		}
			
		Ext.Msg.confirm('확인', '실행하시겠습니까?', function(button) {
			if (button == 'yes') {
				Ext.Ajax.request({
					url : 'sql/I_INV_SUM_TOT_HSPF.jsp',
					method : 'post',
					params : {
						V_TYPE : 'C',
						V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
		    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
		    			V_YYYYMM: Ext.getCmp('V_YYYYMM').getValue(),
					},
					success : function(response) {
						Ext.toast({
							title : ' ',
							timeout : 1000,
							html : '실행완료',
							style : 'text-align: center',
							align : 't',
							width : 130,
						});
						
						var tbController = I_INV_SUM_TOT_HSPF.app.getController("TbButtonController");
			    		tbController.selBtnClick();
					}
				})
			}
		});
	},
	
	xlsxDownClick : function(button, e, eOpts) {
		var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
		var grid = Ext.getCmp('myGrid');
		grid.saveDocumentAs({
			type : 'xlsx',
			title : '월별수불집계현황', // 엑셀내타이틀
			fileName : currentDate + '.xlsx' // 엑셀파일이름
		});
	},

});
