    			V_TYPE: 'SP',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			W_PO_DT_FR : Ext.getCmp('W_PO_DT_FR').getValue(),
    			W_PO_DT_TO : Ext.getCmp('W_PO_DT_TO').getValue(),
