Ext.define('B_STORAGE_LOCATION_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore', 'MyStore1', 'MyStore2'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
    	
    	store.removeAll();
    	store1.removeAll();
    	store2.removeAll();
    	
    	//1번 창고정보조회
    	store.load({
    		params: {
    			V_TYPE: 'SS',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_SL_CD: Ext.getCmp('V_SL_CD').getValue(),
				V_SL_NM: Ext.getCmp('V_SL_NM').getValue(),
    		},
    		callback: function(records,operations,success){
    			//2번 LOCATION정보조회
    			store1.load({
    	    		params: {
    	    			V_TYPE: 'LS',
    	    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    	    			V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    	    			V_SL_CD: Ext.getCmp('V_SL_CD').getValue(),
    					V_SL_NM: Ext.getCmp('V_SL_NM').getValue(),
    	    			V_LC_CD: Ext.getCmp('V_LC_CD').getValue(),
    	    			V_LC_NM: Ext.getCmp('V_LC_NM').getValue()
    	    		},
    	    		callback: function(records,operations,success){
    	    			//3번 RACK정보조회
    	    			store2.load({
    	    				params: {
    	    					V_TYPE: 'RS',
    	    					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
    	    					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    	    					V_SL_CD: Ext.getCmp('V_SL_CD').getValue(),
    	    					V_SL_NM: Ext.getCmp('V_SL_NM').getValue(),
    	    	    			V_LC_CD: Ext.getCmp('V_LC_CD').getValue(),
    	    	    			V_LC_NM: Ext.getCmp('V_LC_NM').getValue(),
    	    					V_RACK_CD: Ext.getCmp('V_RACK_CD').getValue(),
    	    					V_RACK_NM: Ext.getCmp('V_RACK_NM').getValue()
    	    				},
    	    				callback: function(records,operations,success){
    	    				}
    	    			});
    	    		}
    	    	});
    		}
    	});
    	
    	
    },

    saveBtnClick: function(button, e, eOpts) {},

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
        var tab=parent.Ext.getCmp('myTab');
                 var activeTab=tab.getActiveTab();
                 var tabIndex=tab.items.indexOf(activeTab);
                 tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
