Ext.define('Z_AUTH_HSPF.controller.RoleController', {
	extend : 'Ext.app.Controller',

	stores : [ 'RoleStore' ],

	control : {
		"#roleSelBtn" : {
			click : 'roleSelBtnClick'
		},
        "#RoleGrid": {
            beforecelldblclick: 'cellDblClick'
        },
		"winrolepop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},

	roleSelBtnClick : function(button, e, eOpts) {
		var store = this.getRoleStoreStore();
		store.removeAll();
		store.load({
			params : {
				V_P_ROLE_CD : Ext.getCmp('V_P_ROLE_CD').getValue(),
				V_P_ROLE_NM : Ext.getCmp('V_P_ROLE_NM').getValue()
			},
			callback : function(records, operations, success) {
			}
		});
	},
	
	cellDblClick: function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
        Ext.getCmp('V_ROLE_CD').setValue(record.get('ROLE_CD'));

        var win = Ext.WindowManager.getActive();
    	if(win){
    	     win.close();
    	}
    },

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.roleSelBtnClick();
		}
	}

});
