Ext.define('Popup.controller.WinDeptPopController', {
	extend : 'Ext.app.Controller',

	views: ['WinDeptPop'],
	stores: ['WinDeptPopStore'],
	control : {
		"#deptSelBtn" : {
			click : 'deptSelBtnClick'
		},
		"#WinDeptGrid" : {
			beforecelldblclick : 'cellDblClick'
		},
		"windeptpop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},

	deptSelBtnClick : function(button, e, eOpts) {
		var store = this.getWinDeptPopStoreStore();
		store.removeAll();
		store.load({
			params : {
				V_TYPE : 'S',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_DEPT_CD : Ext.getCmp('W_DEPT_CD').getValue(),
				W_DEPT_NM : Ext.getCmp('W_DEPT_NM').getValue()
			},
			callback : function(records, operations, success) {
			}
		});
	},

	cellDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var DEPT_CD = record.get('DEPT_CD');
		var DEPT_NM = record.get('DEPT_NM');
		
		if (Ext.getCmp('fieldType').getValue() == 'Z_USR_INFO_HSPF') { //사용자등록
			var gridRecord =  Ext.getCmp('myGrid');
			gridRecord.actionables['0'].context.record.set('DEPT_CD', DEPT_CD);
			gridRecord.actionables['0'].context.record.set('DEPT_NM', DEPT_NM);
		} else {
			var gridRecord =  Ext.getCmp('myGrid').editingPlugin.getEditor();
			gridRecord.activeField.setValue(DEPT_CD);
		}

		var popWin=  Ext.getCmp('WinDeptPop');
		popWin.destroy();
		
	},
	
	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.deptSelBtnClick();
		}
	},
	
});
