Ext.define('Popup.controller.WinItemDetailPopController', {
    extend: 'Ext.app.Controller',
    
    views: ['WinItemDetailPop'],
	stores: ['WinItemDetailPopStore'],
	control : {
		"#itemDetailSelBtn": {
            click: 'itemDetailSelBtnClick'
		},
		"#WinItemDetailGrid" : {
			beforecelldblclick : 'cellDblClick'
		},
		"winitemdetailpop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},
    
	itemDetailSelBtnClick: function(button, e, eOpts) {
		var store = this.getWinItemDetailPopStoreStore();
		store.removeAll();
		store.load({
			params : {
				V_TYPE : 'S',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_S_BP_CD : Ext.getCmp('W_ITEM_CD').getValue(),
				W_S_BP_NM : Ext.getCmp('W_ITEM_NM').getValue(),
				W_BP_CD : Ext.getCmp('V_BP_CD').getValue()
			},
			callback : function(records, operations, success) {
			}
		});
	},
	
	cellDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var ITEM_CD = record.get('W_ITEM_CD');
		var ITEM_NM = record.get('W_ITEM_NM');
		var SPEC = record.get('W_SPEC');
		var UNIT = record.get('W_UNIT');
		var S_PRC = record.get('W_S_PRC');
		
		var gridRecord =  Ext.getCmp('myGrid').getSelectionModel().getSelection();
		
		gridRecord[0].set('ITEM_CD', ITEM_CD);
		gridRecord[0].set('ITEM_NM', ITEM_NM);
		gridRecord[0].set('SPEC', SPEC);
		gridRecord[0].set('PRICE', S_PRC);
		gridRecord[0].set('UNIT', UNIT);
		
		var popWin=  Ext.getCmp('WinItemDetailPop');
		popWin.destroy();

	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.itemDetailSelBtnClick();
		}
	},

});
