Ext.define('M_PO_PLAN_LOCAL_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore', 'MyStore1', 'MyStore2'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#calcBtn": {
            click: 'calcBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	var store1 = this.getMyStore1Store();
    	var store2 = this.getMyStore2Store();
    	store.removeAll();
    	store1.removeAll();
    	store2.removeAll();
    	store.load({
    		params: {
    			V_TYPE: 'HS',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_YYMMDD: Ext.getCmp('V_YYMMDD').getValue(),
    			V_ITEM_CD: Ext.getCmp('V_ITEM_CD').getValue(),
    			V_ITEM_NM: Ext.getCmp('V_ITEM_NM').getValue(),
    		},
    		callback: function(records,operations,success){
    			Ext.toast({
					title : ' ',
					timeout : 1000,
					html : '조회완료',
					style : 'text-align: center',
					align : 't',
					width : 130,
				});
    		}
    	});
    },

    calcBtnClick: function(button, e, eOpts) {
    	
    	Ext.Ajax.request({
			url : 'sql/m_po_plan_local_hspf.jsp',
			method : 'post',
			params : {
				V_TYPE: 'BT',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_YYMMDD: Ext.getCmp('V_YYMMDD').getValue(),
    			V_ITEM_CD: Ext.getCmp('V_ITEM_CD').getValue(),
    			V_ITEM_NM: Ext.getCmp('V_ITEM_NM').getValue(),
			},
			callback : function(records, operations, success) {
				this.selBtnClick();
			},
			success : function(response) {
				
			},
			scope : this
		});
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
		
	},

    tfEnter: function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.selBtnClick();
		}
    }

});
