Ext.define('S_DN_REQ_HSPF.controller.WinAddRowController1', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn1": {
            click: 'addRowBtn1Click'
        }
    },

    addRowBtn1Click: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore1');
            	var count = store.getCount();
            	var rowCount = Number(Ext.getCmp('rowCount1').getValue());

            	for(var i=0; i<rowCount; i++) {
            		count = count + 1;
            		var rec = Ext.create('S_DN_REQ_HSPF.model.MyModel1', {
            			//USR_ID: parent.Ext.getCmp('GUSER_ID').getValue()
            			SAMPLE_YN : 'N',
            		});
            		store.insert(count-1, rec);
            	}

            	var win = Ext.WindowManager.getActive();
            	if(win){
            	     win.close();
            	}
    }

});
