Ext.define('S_CONT_HSPF.controller.MyRoleGrid4Controller', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore4'
    ],

    control: {
        "#RoleGridAddBtn": {
            click: 'RoleGrid4AddBtnClick'
        },
        "#RoleGrid4xlsxDown": {
            click: 'RoleGrid4xlsxDownClick'
        },
        "#RoleGrid4xmlDown": {
            click: 'RoleGrid4xmlDownClick'
        }
    },

    RoleGrid4AddBtnClick: function(button, e, eOpts) {
        var popup = Ext.create("S_CONT_HSPF.view.WinAddRow4");
        popup.show();
        Ext.getCmp('roleCount').setValue(1);
    },

    RoleGrid4xlsxDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('RoleGrid4');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: 'test', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    },

    RoleGrid4xmlDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('RoleGrid4');
            	grid.saveDocumentAs({
                    type: 'xml',
                    title: 'test', //엑셀내타이틀
                    fileName: currentDate+'.xml' //엑셀파일이름
        		});
    }

});
