	/*[조회]버튼*/
	selBtnClick : function(button, e, eOpts) {
		var store = this.getMyStoreStore();
		var store1 = this.getMyStore1Store();
		store.removeAll();
		store.load({
			params : {
				V_TYPE : 'HS',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_BP_CD : parent.Ext.getCmp('GBP_CD').getValue(),
				V_DLVY_DT_FR : Ext.getCmp('V_DLVY_DT_FR').getValue(),
				V_DLVY_DT_TO : Ext.getCmp('V_DLVY_DT_TO').getValue(),
				V_PO_NO : Ext.getCmp('V_PO_NO').getValue(),
				V_PO_DT_FR : Ext.getCmp('V_PO_DT_FR').getValue(),
				V_PO_DT_TO : Ext.getCmp('V_PO_DT_TO').getValue(),
				V_ITEM_CD : Ext.getCmp('V_ITEM_CD').getValue(),
				V_ITEM_NM : Ext.getCmp('V_ITEM_NM').getValue(),
				V_M_BP_CD : Ext.getCmp('V_M_BP_CD').getValue(),
				V_M_BP_NM : Ext.getCmp('V_M_BP_NM').getValue(),
				V_ASN_NO_TF : Ext.getCmp('V_ASN_NO_TF').getValue(),
				V_BL_NO : Ext.getCmp('V_BL_NO').getValue(),
			},
			callback : function(records, operations, success) {
				store1.removeAll();
			}
		})
	},

