Ext.define('CUS_DN_DAILY.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },
    onLaunch : function(application) {
    	var tbController = CUS_DN_DAILY.app.getController("TbButtonController");
		tbController.selBtnClick();
    },
    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
		store.removeAll();
		
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    		
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_S_BP_CD: parent.Ext.getCmp('GBP_CD').getValue(),
    			V_ITEM_NM: Ext.getCmp('V_ITEM_NM').getValue(),
    			V_SL_CD: Ext.getCmp('V_SL_CD').getValue(),
    			V_DN_DT: Ext.getCmp('V_DN_DT').getValue(),
    		},
    		callback: function(records,operations,success){
    	    	
    		}
    	});
    	
    	
    	
    },
    saveBtnClick: function(button, e, eOpts) {
    	var store = Ext.getStore('MyStore');
    	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	var flag = '';
    	var msg = '';
    	for(var i=0; i< gridRecord.length; i++) {
	    		if(gridRecord[i].get('DN_REQ_BOX_QTY') == null || gridRecord[i].get('DN_REQ_BOX_QTY') == 0) {
	    			flag = 'F';
	    			msg = '출고요청 박스수량을 확인하세요.';
	    		} else if(gridRecord[i].get('DN_QTY') == null || gridRecord[i].get('DN_QTY') == 0) {
	    			flag = 'F';
	    			msg = '출고요청중량을 확인하세요.';
	    		} else if(gridRecord[i].get('DN_REQ_DT') == null) {
	    			flag = 'F';
	    			msg = '출고요청일을 확인하세요.';
	    		} else if(gridRecord[i].get('MVMT_NO') == null) {
	    			flag = 'F';
	    			msg = '미입고 품목입니다.';
	    		} else {
	    			flag = 'T';
	    		}
    	}
    	
    	if(flag == 'T') {
    		Ext.MessageBox.confirm('확인', '출고요청 하시겠습니까?', function(btn) {
        		if(btn == 'yes') {
        			for(var i=0; i< gridRecord.length; i++) {
        	    		if(gridRecord[i].get('V_TYPE') == 'V') {
        	    			gridRecord[i].set('V_TYPE', 'I');
        	    		}
        			}
        			
        			store.sync({
        				params : {
        					V_TYPE : 'SYNC',
        					V_SP_TYPE: 'DN_REQ',
        					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
        					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
        					V_S_BP_CD: parent.Ext.getCmp('GBP_CD').getValue(),
        				},
        				callback : function(records, operation, success) {
        					var res = records.operations[0]._response.responseText;
        					
        					if(res.match('Exception')) {
        						Ext.Msg.alert('확인', res);
        					} else {
        						var tbController = CUS_DN_REQ.app.getController("TbButtonController");
        			    		tbController.selBtnClick();
        					}
        				}, 
        				success: function() {
        				}
        			});
        		}
        	});
    	} else {
    		Ext.Msg.alert('확인', msg);
    	}
    	
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    },
    

});
