    myGridClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {

		var store1 = Ext.getStore('MyStore1');
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var mvmtArr = new Array();
		
		for(var i=0; i<gridRecord.length; i++) {
			if(!mvmtArr.includes(gridRecord[i].get('MVMT_NO'))) {
				mvmtArr.push(gridRecord[i].get('MVMT_NO'));
			}
		}

		var MVMT_NO = '\'';
		
		for(var i=0; i<mvmtArr.length; i++) {
			MVMT_NO += mvmtArr[i] +'\',\'';
		}
		
		MVMT_NO = '(' + MVMT_NO.substr(0, MVMT_NO.length -2) + ')'; 
		
		if (record.get('DN_BOX_QTY') != 0 && (gridRecord.length > 0)) {

		store1.load({
    		params: {
    			V_TYPE: 'SD',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_MVMT_NO: MVMT_NO
    		},
    		callback: function(records,operations,success){
    			
    		}
		});
		} else {
			store1.removeAll();
		}

	},
	addColBtnClick: function(button, e, eOpts) {
		
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		
		//if(gridRecord.length == 1) {
			var popup = Ext.create("M_GR_TO_DN_DISTR.view.MyWindow3");

			var width = Ext.getBody().getViewSize().width - 200;
			var height = Ext.getBody().getViewSize().height - 200;
			popup.setSize(width, height);
			popup.center();

			popup.show();
			
			Ext.getCmp('W3_S_BP_CD').setValue(gridRecord[0].get('S_BP_CD'));
			Ext.getCmp('W3_S_BP_NM').setValue(gridRecord[0].get('S_BP_NM'));
			Ext.getCmp('W_MVMT_NO').setValue(gridRecord[0].get('MVMT_NO'));
			
			Ext.Ajax.request({
				url : '/HSPF01/HSPF02/mm2/po/m_col_distb/sql/M_COL_DISTB.jsp',
				method : 'post',
				params : {
					V_TYPE : 'ADD_COL_S',
					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					//V_MVMT_NO: gridRecord[0].get('MVMT_NO')
					V_BL_DOC_NO: gridRecord[0].get('BL_DOC_NO')
				},
				success : function(response) {
			    	var jsonResult = Ext.JSON.decode(response.responseText);
					Ext.getCmp('V_NEW_COL_NO').setValue(jsonResult.resultList[0].COL_NO);
					Ext.getCmp('V_NEW_MAST_DB_NO').setValue(jsonResult.resultList[0].MAST_DB_NO);
					Ext.getCmp('V_OLD_MAST_DB_NO').setValue(jsonResult.resultList[0].OLD_MAST_DB_NO);
					Ext.getCmp('V_COL_MGM_NO').setValue(jsonResult.resultList[0].COL_MGM_NO);
					
					var colStore = Ext.getStore('ColStore');
					colStore.removeAll();
					
					if(colStore.getCount() == 0) {
						var count = colStore.getCount();

				    	var rec = Ext.create('M_GR_TO_DN_DISTR.model.MyModel', {
				    		CONT_REMARK: jsonResult.resultList[0].CONT_REMARK,
//				    		COL_TYPE: jsonResult.resultList[0].COL_TYPE,
//				    		COL_TYPE: jsonResult.resultList[0].ADD_COL_MAST_DB_NO ? jsonResult.resultList[0].COL_TYPE : 'A', // DEFAULT는 A(현금)
				    		COL_TYPE: jsonResult.resultList[0].ADD_COL_MAST_DB_NO ? jsonResult.resultList[0].COL_TYPE : 'G', // DEFAULT는 E(추가현금)
				    		BL_DOC_NO: jsonResult.resultList[0].BL_DOC_NO,
						});
				    	
				    	colStore.insert(count-1, rec);
				    	colStore.reload();
					}
			    	
			    }
			});
		/*} else {
			Ext.Msg.alert('확인', '추가담보를 설정할 내역을 한 개 선택하세요.')
		}*/
    },
