Ext.define('M_BL_STEEL.controller.LcChargePopController', {
    extend: 'Ext.app.Controller',
    stores : [ 'MyStore1', 'PopStore', 'LcChargePopStore'],
    
    control: {
		"#w_lcSelBtn" : {
			click : 'w_lcSelBtnClick'
		},
		'#w_lcRegBtn' : {
			click : 'w_lcRegBtnClick'
		},
		'#lcChargePopGrid': {
			celldblclick: 'w_popGridDblClick'
		},
		"[name=bl_search]": {
            specialkey: 'tfEnter'
        }
	},

	w_lcSelBtnClick: function(button, e, eOpts) {
		var popStore = this.getStore('LcChargePopStore');
		popStore.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_LC_DOC_NO : Ext.getCmp('W_LC_DOC_NO').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});
	},
	
	w_lcRegBtnClick: function(button, e, eOpts) {
		this.w_popGridDblClick();
	},
	
	w_popGridDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		Ext.getCmp('V_REC_CHARGE_NO').setValue(record.get('MAST_CHARGE_NO'));

		
		var popWin=  Ext.getCmp('WinLcChargePop');
		popWin.destroy();
	},
	
	tfEnter: function(field, e, eOpts) {
    	if (e.getKey() == e.ENTER) {
    		this.w_blSelBtnClick();
    	}
}
	

});
