Ext.define('M_GR_STEEL2.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore2');
            	var count = store.getCount();
            	var rowCount = Number(Ext.getCmp('rowCount').getValue());
            	var gridRecord = Ext.getCmp('myGrid1').getSelectionModel().getSelection();

            	for(var i=0; i<rowCount; i++) {
            		count = count + 1;
            		var rec = Ext.create('M_GR_STEEL2.model.MyModel2', {
            			MVMT_NO: gridRecord[0].get('MVMT_NO'),
            			ITEM_GROUP_CD: gridRecord[0].get('ITEM_CD'),
            			PRICE: gridRecord[0].get('PRICE'),
            		});
            		store.insert(count-1, rec);
            	}

            	var win = Ext.WindowManager.getActive();
            	if(win){
            	     win.close();
            	}
    }

});
