Ext.define('S_BILL_DT_CHG.controller.TbButtonController', {
    extend: 'Ext.app.Controller',
    stores: ['MyStore'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	store.removeAll();
    	
    	store.load({
    		params: {
    			V_TYPE: 'S',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_S_BP_CD : Ext.getCmp('V_S_BP_CD').getValue(),
    			V_DEPT_TYPE : Ext.getCmp('V_DEPT_TYPE').getValue(),
    			V_BILL_DT_FR: Ext.getCmp('V_BILL_DT_FR').getValue(),
    			V_BILL_DT_TO: Ext.getCmp('V_BILL_DT_TO').getValue(),
    			V_DN_IS_DT_FR: Ext.getCmp('V_DN_IS_DT_FR').getValue(),
    			V_DN_IS_DT_TO: Ext.getCmp('V_DN_IS_DT_TO').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});
    	
    },

    saveBtnClick: function(button, e, eOpts) {
    	var store = Ext.getStore('MyStore');
    	
    	store.each(function(record, idx) {
			if(record.isDirty()) {
				record.set('V_TYPE', 'U');
			}
		})

		store.sync({
			params : {
				V_TYPE : 'U',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
			},
			callback : function(records, operation, success) {
				var res = records.operations[0]._response.responseText;

				if (res.match('Exception')) {
					Ext.Msg.alert('확인', res);
				} else {
					store.reload();
					Ext.toast({
						title : ' ',
						timeout : 1000,
						html : '저장완료',
						style : 'text-align: center',
						align : 't',
						width : 130,
					});
				}
				
			},
			success : function(response) {
			}
		});
    },

    clrBtnClick: function(button, e, eOpts) {
    	var store = Ext.getStore('MyStore');
		store.removeAll();
		
		var nows = new Date();
		Ext.getCmp('V_S_BP_CD').setValue('');
		Ext.getCmp('V_S_BP_NM').setValue('');
		Ext.getCmp('V_BILL_DT_TO').setValue(nows);
		Ext.getCmp('V_DN_IS_DT_TO').setValue(nows)
		
		nows.setDate(1);
		Ext.getCmp('V_BILL_DT_FR').setValue(nows);
		Ext.getCmp('V_DN_IS_DT_FR').setValue(nows);
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
    },

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.selBtnClick();
		}
	}

});
