	xlsxDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
    	var grid = Ext.getCmp('myGrid');
    	grid.saveDocumentAs({
            type: 'xlsx',
            title: '품의등록', //엑셀내타이틀
            fileName: currentDate+'.xlsx' //엑셀파일이름
		});
},
	gridAddBtnClick: function() {
		var msg = '';
		var flag = 'T';

		if (Ext.getCmp('V_APPROV_MGM_NO').getValue() == '' || Ext.getCmp('V_APPROV_MGM_NO').getValue() == null) {
			flag = 'F';
			msg = '품의관리번호를 입력하세요.';
		} else {
			flag = 'T';
		}

		if (flag == 'T') {
			var popup = Ext.create("Y_APPROV_STEEL.view.WinAddRow");
			popup.show();
			Ext.getCmp('rowCount').setValue(1);
		} else {
			Ext.Msg.alert('확인', msg);
		}
	},
	
	gridDelBtnClick: function() {
		var store = this.getMyStoreStore();
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var flag = '';
		
		Ext.MessageBox.confirm('확인', '삭제하시겠습니까?', function(btn) {
			if(btn == 'yes') {
				for(var i=0; i<gridRecord.length; i++) {
					if(gridRecord[i].data['V_TYPE'] == 'V' && gridRecord[i].phantom == true) {
						store.remove(gridRecord[i]);
						flag = 'F';
					} else if(gridRecord[i].data['V_TYPE'] == 'V' && gridRecord[i].phantom == false) {
						gridRecord[i].set('V_TYPE', 'D');
						flag = 'T';
					}
				}
				
				store.sync({
					params : {
						V_TAB_TYPE: 'T1',
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_APPROV_MGM_NO: Ext.getCmp('V_APPROV_MGM_NO').getValue(),
					},
					callback : function(records, operation, success) {
						var res = records.operations[0]._response.responseText;
						
						if(res.match('Exception')) {
							Ext.Msg.alert('확인', res);
						} else {
							if(flag == 'T') {
								store.reload();
							}
						}
					}, 
					success: function() {
					}
				});
			
			}
		});
		
	},
	
	gridSaveBtnClick: function() {
		var store = this.getMyStoreStore(); 
		var flag = '';
		var msg = '';
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		
		var strCont = new Array();
		var numberCont = 0;
		
			store.each(function(record, idx) {
				if(record.phantom == true) {
					record.set('V_TYPE', 'I');
				} else if (record.phantom == false) {
					record.set('V_TYPE', 'U');
				}
				
				if(record.get('CONT_QTY') == null || record.get('CONT_QTY') == undefined) {
					record.set('CONT_QTY', '');
				}
				
				if (record.get('PO_QTY') == 0) {
					flag = 'F';
					msg = '발주수량을 확인하세요.'; 
					return false;
				} 
				else if (record.get('PO_AMT') == 0) {
					flag = 'F';
					msg = '발주금액을 확인하세요.'; 
					return false;
				} 
				else if (record.get('ITEM_NM') == null || record.get('ITEM_NM') == undefined || record.get('ITEM_NM') == '') {
					flag = 'F';
					msg = '품목그룹을 선택해주세요.'; 
					return false;
				} 
				else if (record.get('M_BP_CD') == null || record.get('M_BP_CD') == undefined || record.get('M_BP_CD') == '') {
					flag = 'F';
					msg = '매입처를 입력해주세요.'; 
					return false;
				} 
				else if (record.get('M_BP_DESC') == null || record.get('M_BP_DESC') == undefined || record.get('M_BP_DESC') == '') {
					flag = 'F';
					msg = '매입품의명을 입력해주세요.'; 
					return false;
				} 
				else if (record.get('M_BP_SEQ') == null || record.get('M_BP_SEQ') == undefined || record.get('M_BP_SEQ') == '') {
					flag = 'F';
					msg = '매입품의차수를 입력해주세요.'; 
					return false;
				} 
				else if (record.get('USANCE_TYPE') == null || record.get('USANCE_TYPE') == undefined || record.get('USANCE_TYPE') == '') {
					flag = 'F';
					msg = 'USANCE조건을 입력해주세요.'; 
					return false;
				} 
				else if (record.get('PAY_METH') == null || record.get('PAY_METH') == undefined || record.get('PAY_METH') == '') {
					flag = 'F';
					msg = '가격조건을 입력해주세요.'; 
					return false;
				} 
				else {
					flag = 'T';
				}
			});
			
			if(flag == 'T') {
				store.sync({
					params : {
						V_TAB_TYPE: 'T1',
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
						V_APPROV_MGM_NO: Ext.getCmp('V_APPROV_MGM_NO').getValue(),
					},
					callback : function(records, operation, success) {
						var res = records.operations[0]._response.responseText;
						
						if(res.match('Exception')) {
							Ext.Msg.alert('확인', res);
						} else {
							Y_APPROV_STEEL.app.getController("TbButtonController").selBtnClick();
						}
					}, 
					success: function() {
					}
				});
			} else {
				Ext.Msg.alert('확인', msg);
			}
			
	},
