Ext.define('M_COL_ACCPT_HSPF.controller.TbButtonController', {
	extend : 'Ext.app.Controller',
	stores : [ 'MyStore', 'MyStore1' ],
	control : {
		"#selBtn" : {
			click : 'selBtnClick'
		},
		"#saveBtn" : {
			click : 'saveBtnClick'
		},
		"#clrBtn" : {
			click : 'clrBtnClick'
		},
		"#clsBtn" : {
			click : 'clsBtnClick'
		},
		"myform textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},

	selBtnClick : function(button, e, eOpts) {
		var store = this.getMyStoreStore();
		var store1 = this.getMyStore1Store();

		store.removeAll();
		store1.removeAll();

		// [상단] 조회
		store.load({
			params : {
				V_TYPE : 'SH',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_DEPT_CD : Ext.getCmp('V_DEPT_CD').getValue(),
				V_COL_DT_FR : Ext.getCmp('V_COL_DT_FR').getValue(),
				V_COL_DT_TO : Ext.getCmp('V_COL_DT_TO').getValue(),
				V_S_BP_CD : Ext.getCmp('V_S_BP_NM').getValue(),
				V_CFM_YN : Ext.getCmp('rbY').getValue() ? 'Y' : Ext.getCmp('rbN').getValue() ? 'N' : ''
			},
			callback : function(records, operations, success) {
			}
		});
	},

	saveBtnClick : function(button, e, eOpts) {
		var store = Ext.getStore('MyStore');

		store.each(function(record, idx) {
			if(record.isDirty()) {
				record.set('V_TYPE', 'A');
			}
		})
		
		Ext.Msg.confirm('확인', '저장하시겠습니까?', function(button) {
			if (button == 'yes') {
				store.sync({
					params : {
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					},
					callback : function(records, operation, success) {
						var tbController = M_COL_ACCPT_HSPF.app.getController("TbButtonController");
						tbController.selBtnClick();
					},
					success : function(response) {
						Ext.toast({
							title : ' ',
							timeout : 1000,
							html : '저장완료',
							style : 'text-align: center',
							align : 't',
							width : 130,
						});
						
					}
				});
			}
		});
	},

	clrBtnClick : function(button, e, eOpts) {
		var store = this.getMyStoreStore();
		var store1 = this.getMyStore1Store();

		store.removeAll();
		store1.removeAll();

		Ext.getCmp('V_S_BP_CD').setValue('');
		Ext.getCmp('V_S_BP_NM').setValue('');
	},

	clsBtnClick : function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.selBtnClick();
		}
	},

});
