Ext.define('M_COLLATERAL_MST_HSPF.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: ['MyStore'],
    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#clrBtn": {
            click: 'clrBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "myform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        },
    },

    selBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	store.removeAll();
    	
    	Ext.Ajax.request({
			url : 'sql/M_COLLATERAL_MST_HSPF.jsp',
			method : 'post',
			params : {
				V_TYPE: 'SH',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			V_COLLATERAL_NO: Ext.getCmp('V_COLLATERAL_NO').getValue(),
			},
			success : function(response) {
				var res = Ext.JSON.decode(response.responseText).resultList[0];

				if(res != null){
					Ext.getCmp('V_COL_NO').setValue(Ext.getCmp('V_COLLATERAL_NO').getValue());
					Ext.getCmp('V_DEPT_CD').setValue(res.DEPT_CD);
					Ext.getCmp('V_BP_NM').setValue(res.BP_NM);
					Ext.getCmp('V_ASGN_DT').setValue(res.ASGN_DT);
					Ext.getCmp('V_RENEW_DT').setValue(res.RENEW_DT);
					Ext.getCmp('V_EFFECTIVE_DT').setValue(res.EFFECTIVE_DT);
					Ext.getCmp('V_DEL_DT').setValue(res.DEL_DT);
					Ext.getCmp('V_EXPIRY_DT').setValue(res.EXPIRY_DT);
					Ext.getCmp('V_STOCK_NO').setValue(res.STOCK_NO);
					Ext.getCmp('V_WARNT_ORG_NM').setValue(res.WARNT_ORG_NM);
					Ext.getCmp('V_DB_TYPE2').setValue(res.DB_TYPE2);
					Ext.getCmp('V_ASGN_AMT').setValue(res.ASGN_AMT);
					Ext.getCmp('V_REMARK').setValue(res.REMARK);
					
					Ext.getCmp('V_BP_NM').setDisabled(true);
					Ext.getCmp('V_DB_TYPE2').setDisabled(true);
					Ext.getCmp('V_DEPT_CD').setDisabled(true);
					Ext.getCmp('V_ASGN_DT').setDisabled(true);
					Ext.getCmp('V_RENEW_DT').setDisabled(true);
					Ext.getCmp('V_EFFECTIVE_DT').setDisabled(true);
					Ext.getCmp('V_DEL_DT').setDisabled(true);
					Ext.getCmp('V_EXPIRY_DT').setDisabled(true);
//					Ext.getCmp('V_COL_NO').setEditable(false);
					Ext.getCmp('V_STOCK_NO').setEditable(false);
					Ext.getCmp('V_WARNT_ORG_NM').setEditable(false);
					Ext.getCmp('V_ASGN_AMT').setEditable(false);
					Ext.getCmp('V_REMARK').setEditable(false);
					
					Ext.getCmp('saveBtn').setDisabled(true);
					
				// [첫번째 그리드] 조회
				store.load({
		    		params: {
		    			V_TYPE: 'DS',
		    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
		    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
		    			V_COLLATERAL_NO: Ext.getCmp('V_COLLATERAL_NO').getValue(),
		    		},
		    		callback: function(records,operations,success){
		    		}
		    	});

				}
			}
		});

    },

    saveBtnClick: function(button, e, eOpts) {
    	Ext.Ajax.request({
			url : 'sql/M_COLLATERAL_MST_HSPF.jsp',
			method : 'post',
			params : {
				V_TYPE: 'IH',
    			V_COMP_ID: parent.Ext.getCmp('GCOMP_ID').getValue(),
    			V_USR_ID: parent.Ext.getCmp('GUSER_ID').getValue(),
    			
    			V_DB_TYPE2: Ext.getCmp('V_DB_TYPE2').getValue(),
    			V_DEPT_CD: Ext.getCmp('V_DEPT_CD').getValue(),
    			V_STOCK_NO: Ext.getCmp('V_STOCK_NO').getValue(),
    			V_WARNT_ORG_NM: Ext.getCmp('V_WARNT_ORG_NM').getValue(),
    			V_REMARK: Ext.getCmp('V_REMARK').getValue(),
    			V_BP_CD: Ext.getCmp('V_BP_CD').getValue(),
    			
    			V_ASGN_AMT: Ext.getCmp('V_ASGN_AMT').getValue(),
    			V_ASGN_DT: Ext.getCmp('V_ASGN_DT').getValue(),
    			V_EFFECTIVE_DT: Ext.getCmp('V_EFFECTIVE_DT').getValue(),
    			V_EXPIRY_DT: Ext.getCmp('V_EXPIRY_DT').getValue(),
    			V_RENEW_DT: Ext.getCmp('V_RENEW_DT').getValue(),
    			V_DEL_DT: Ext.getCmp('V_DEL_DT').getValue(),
			},
			success : function(response) {
				Ext.toast({
					title : ' ',
					timeout : 1000,
					html : '저장완료',
					style : 'text-align: center',
					align : 't',
					width : 130,
				});
				
				Ext.getCmp('V_COLLATERAL_NO').setValue(Ext.JSON.decode(response.responseText).COLLATERAL_NO);
				Ext.getCmp('saveBtn').setDisabled(true);

				var tbController = M_COLLATERAL_MST_HSPF.app.getController("TbButtonController");
	    		tbController.selBtnClick();
			}
		});
    	
    },

    clrBtnClick: function(button, e, eOpts) {
    	var store = this.getMyStoreStore();
    	store.removeAll();

    	Ext.getCmp('V_COLLATERAL_NO').setValue('');
    	Ext.getCmp('V_COL_NO').setValue('');
    	Ext.getCmp('V_DEPT_CD').setValue('');
    	Ext.getCmp('V_BP_CD').setValue('');
		Ext.getCmp('V_BP_NM').setValue('');
		Ext.getCmp('V_ASGN_DT').setValue('');
		Ext.getCmp('V_RENEW_DT').setValue('');
		Ext.getCmp('V_EFFECTIVE_DT').setValue('');
		Ext.getCmp('V_DEL_DT').setValue('');
		Ext.getCmp('V_EXPIRY_DT').setValue('');
		Ext.getCmp('V_STOCK_NO').setValue('');
		Ext.getCmp('V_WARNT_ORG_NM').setValue('');
		Ext.getCmp('V_DB_TYPE2').setValue('');
		Ext.getCmp('V_ASGN_AMT').setValue('');
		Ext.getCmp('V_REMARK').setValue('');
		
		Ext.getCmp('V_BP_NM').setDisabled(false);
		Ext.getCmp('V_DB_TYPE2').setDisabled(false);
		Ext.getCmp('V_DEPT_CD').setDisabled(false);
		Ext.getCmp('V_ASGN_DT').setDisabled(false);
		Ext.getCmp('V_RENEW_DT').setDisabled(false);
		Ext.getCmp('V_EFFECTIVE_DT').setDisabled(false);
		Ext.getCmp('V_DEL_DT').setDisabled(false);
		Ext.getCmp('V_EXPIRY_DT').setDisabled(false);
//    	Ext.getCmp('V_COL_NO').setEditable(true);
		Ext.getCmp('V_STOCK_NO').setEditable(true);
		Ext.getCmp('V_WARNT_ORG_NM').setEditable(true);
		Ext.getCmp('V_ASGN_AMT').setEditable(true);
		Ext.getCmp('V_REMARK').setEditable(true);
		
		Ext.getCmp('saveBtn').setDisabled(false);
    	
    },

    clsBtnClick: function(button, e, eOpts) {
		var tab = parent.Ext.getCmp('myTab');
		var activeTab = tab.getActiveTab();
		var tabIndex = tab.items.indexOf(activeTab);
		tab.remove(tabIndex, true);
	},

    tfEnter: function(field, e, eOpts) {
        if (e.getKey() == e.ENTER) {
        	this.selBtnClick();
        }
    },  
    
});
