Ext.define('naporeqmgm.controller.MyGridController', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore'
    ],

    control: {
        "#gridAddBtn": {
            click: 'gridAddBtnClick'
        },
        "#gridDelBtn": {
            click: 'gridDelBtnClick'
        },
        "#xlsxDown": {
            click: 'xlsxDownClick'
        },
        "#xmlDown": {
            click: 'xmlDownClick'
        },
        "#poPrintBtn": {
            click: 'poPrintBtnClick'
        },
    },

    gridAddBtnClick: function(button, e, eOpts) {
        //var popup = Ext.create("B_COMP_HSPF.view.WinAddRow");
        //popup.show();
        //Ext.getCmp('rowCount').setValue(1);
    },

    gridDelBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
            	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();

            	if(gridRecord.length > 0) {
            		Ext.Msg.confirm('확인','삭제하시겠습니까?', function(button){
            			if(button == 'yes') {
            				for(var i=0; i<gridRecord.length; i++) {
            		    		if(gridRecord[i].data['V_TYPE']=='V') {
            		    			gridRecord[i].data['V_TYPE'] = 'D';
            		    		}
            		    	}
            		    	store.sync({
            					params: {
            						V_USR_ID : 'admin',//parent.Ext.getCmp('GUSER_ID').getValue(),
            					},
            		    		callback: function(records, operation, success) {
            		    			store.reload();
            					}
            		    	});
            			}
            		});
            	}
    },

    xlsxDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: '해외법인 주문접수', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    },

    xmlDownClick: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid');
            	grid.saveDocumentAs({
                    type: 'xml',
                    title: 'test', //엑셀내타이틀
                    fileName: currentDate+'.xml' //엑셀파일이름
        		});
    },
    
    /* 발주서 출력*/
    poPrintBtnClick: function() {
    	var msg = '';
    	var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
    	
    	if(gridRecord.length > 1){
    		Ext.Msg.alert('확인', '행 하나만 선택해주세요');
    	}
    	else if(gridRecord.length < 1){
    		Ext.Msg.alert('확인', '행을 선택해주세요');
    	}
    	else{
    		if(gridRecord[0].data['HS_PO_NO'] == '' || gridRecord[0].data['HS_PO_NO'] == null || gridRecord[0].data['HS_PO_NO'] == undefined){
    			Ext.Msg.alert('확인', '주문접수 후 출력하세요.');
    		}
    		else{
    			
    		
	    		if(parent.Ext.getCmp('MAIN_SERVER_YN').getValue() == 'Y'){
	        		var url_DO = 'http://123.142.124.170:8080/aireport/on_server/M_ORDER_PAPER.jsp?reportParams=pdfsavename:';
	    		}
	    		else{
	    	    	var url_DO = 'http://123.142.124.170:8080/aireport/on_server/M_ORDER_PAPER_TEST.jsp?reportParams=pdfsavename:';
	    		}
	        	var url = '';
	        	var MAST_PO_NO = '';
	        	var HS_PO_NO = gridRecord[0].data['HS_PO_NO'];
	        	
	    		
	    		var hIndex = HS_PO_NO.indexOf('-')
	    		
	    		MAST_PO_NO = HS_PO_NO.substring(0,hIndex);
	    		
	    		url = url_DO + MAST_PO_NO + '&MAST_PO_NO=' + MAST_PO_NO;
	
	    		
	    		
	    		
	    		
				var myWin = new Ext.Window({
					title : '발주서',
					html : '<iframe name="xxx" border =0 src="'+url+'" width="100%" height="100%"></iframe>',
					width : 1000,
					height : 768,
					modal : true
				});
				myWin.show();
				myWin.setSize(Ext.getBody().getViewSize());
				myWin.setPagePosition(0, 0);
    		}
			
    	}
    },
    

});
