Ext.define('B_ITEM_HSPF.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
            	var count = store.getCount();
            	var rowCount = Number(Ext.getCmp('rowCount').getValue());

            	for(var i=0; i<rowCount; i++) {
            		count = count + 1;
            		var rec = Ext.create('B_ITEM_HSPF.model.MyModel', {
            			MIN_PO_QTY: 0,
            			SAFE_QTY: 0,
            			MAX_PACK_QTY: 0,
            			MID_PACK_QTY: 0,
            			MIN_PACK_QTY: 0, 
            			SUPP_LT_DT: 0,
            			USE_YN : 'Y',
            			ITEM_CALC_YN : 'N',
            			SEND_MAIL_YN : 'N',
            		});
            		store.insert(i, rec);
//            		store.insert(count-i, rec);
            	}

            	var popWin=  Ext.getCmp('WinAddRow');
            	popWin.destroy();
            	
            	Ext.getCmp('myGrid').getView().focusRow(0);
    }

});
