Ext.define('S_BL_DOC_TOT_HSPF.controller.MyGridController2', {
	extend : 'Ext.app.Controller',
	stores : [ 'MyStore2' ],
	control : {
		"#gridAddBtn2" : {
			click : 'gridAddBtn2Click'
		},
		"#gridDelBtn2" : {
			click : 'gridDelBtn2Click'
		},
		"#gridSaveBtn2" : {
			click : 'gridSaveBtn2Click'
		},
		"#gridDistBtn" : {
			click : 'gridDistBtnClick'
		},
		"#xlsxDown2" : {
			click : 'xlsxDown2Click'
		}
	},
	
	gridAddBtn2Click : function(button, e, eOpts) {
		if (!!!Ext.getCmp('V_INV_NO').getValue()) {
			Ext.Msg.alert('확인', '인보이스 등록 후 가능합니다.');
			return;
		}
		
		var popup = Ext.create("S_BL_DOC_TOT_HSPF.view.WinAddRow");
		popup.show();
		Ext.getCmp('rowCount').setValue(1);
	},
	
	gridSaveBtn2Click : function(button, e, eOpts) {
		var store1 = Ext.getStore('MyStore1');
		var store2 = Ext.getStore('MyStore2');
		var flag = '';
		var msg = '';
		
		if (!!!Ext.getCmp('V_INV_NO').getValue()) {
			flag = 'F';
			msg = '인보이스 등록 후 가능합니다.';
		} else if (Ext.getCmp('V_CFM_YN').getValue() == 'Y') {
			flag = 'F';
			msg = '확정취소 후 가능합니다.';
		} else {
			flag = 'T';
			
			store2.each(function(rec,idx) {
				if (!!!rec.get('INV_NO')) {
					flag = 'F';
					msg = '인보이스 내역이 저장되지 않았습니다.';
					return false;
				} else {
					flag = 'T';
					
//					if (!!rec.get('INV_NO') && rec.get('V_TYPE') != 'I') {
						rec.set('V_TYPE', 'U');
//					}
				}
			});
		}
		
		if (flag == 'T') {
			Ext.Msg.confirm('확인', '저장하시겠습니까?', function(button) {
				if (button == 'yes') {
					store2.sync({ 
						params : {
							V_TYPE : 'SYNC2',
							V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
							V_INV_NO: Ext.getCmp('V_INV_NO').getValue(),
						},
						callback : function(records, operation, success) {
				    		var tbController = S_BL_DOC_TOT_HSPF.app.getController("TbButtonController");
				    		tbController.selBtnClick();
						}, 
					});
				}
			});
		} else {
			Ext.Msg.alert('확인', msg);
		}
		
	},
	
	gridDelBtn2Click : function(button, e, eOpts) {
		var store1 = Ext.getStore('MyStore1');
		var store2 = Ext.getStore('MyStore2');
		var flag = '';
		var msg = '';
		
		var gridRecord2 = Ext.getCmp('myGrid2').getSelectionModel().getSelection();
		for(var i=0; i<gridRecord2.length; i++) {
			if(gridRecord2[i].get('V_TYPE') == 'I') {
				store1.remove(gridRecord2[i]);
				store2.remove(gridRecord2[i]);
			} else {
				gridRecord2[i].set('V_TYPE', 'D');
				flag = 'T';
			}
		}
		
		if(flag == 'T') {
			Ext.Msg.confirm('확인', '삭제하시겠습니까?', function(button) {
				if (button == 'yes') {
					
					store2.sync({
						params : {
							V_TYPE : 'SYNC2',
							V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
							V_INV_NO: Ext.getCmp('V_INV_NO').getValue(),
						},
						callback : function(records, operation, success) {
							store1.reload();
							store2.reload();
						}, 
						success: function() {
						}
					});
				}
			});
		}
	},
	
	gridDistBtnClick : function(button, e, eOpts) {
		var store2 = Ext.getStore('MyStore2');
		var totalGrossWgt = Ext.getCmp('V_TOTAL_GROSS_WGT').getValue();
		var totalNetWgt = 0;
		
		store2.each(function(rec,idx) {
			totalNetWgt += rec.get('NET_WGT');
		});
		
		var diffRt = totalGrossWgt / totalNetWgt;
		var storeTotGrossWgt = 0;
		store2.each(function(rec,idx) {
			var grossWgt = Math.round(rec.get('NET_WGT') * diffRt);
			storeTotGrossWgt += grossWgt;
			rec.set('GROSS_WGT', grossWgt);
		});
		
		var diffWgt = totalGrossWgt - storeTotGrossWgt;
		if(diffWgt != 0){
			var firstGrossWgt = store2.getData().items[store2.getData().length-1];
			firstGrossWgt.set('GROSS_WGT', firstGrossWgt.get('GROSS_WGT')+diffWgt);
		}
		
	},

	xlsxDown2Click: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
    	var grid = Ext.getCmp('myGrid2');
    	grid.saveDocumentAs({
            type: 'xlsx',
            title: 'Packing 내역', //엑셀내타이틀
            fileName: currentDate+'.xlsx' //엑셀파일이름
		});
    }
});
