Ext.define('M_GR_TOT_HSPF2.controller.MyGridController', {
	extend : 'Ext.app.Controller',

	control : {
		"#gridGrBtn" : {
			click : 'gridGrBtnClick'
		},
		"#gridAddBtn" : {
			click : 'gridAddBtnClick'
		},
		"#gridDelBtn" : {
			click : 'gridDelBtnClick'
		},
		"#xlsxDown" : {
			click : 'xlsxDownClick'
		},
		"#myGrid" : {
			cellclick : 'myGridClick'
		}
	},

	// 입고등록버튼
	gridGrBtnClick : function(button, e, eOpts) {
    	
    	var store = Ext.getStore('MyStore');
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var flag = '';
		var msg = '';
		
		for(var i=0; i<gridRecord.length; i++) {
//			if(gridRecord[i].get('SL_CD') == null || gridRecord[i].get('SL_CD') == undefined || gridRecord[i].get('SL_CD') == '') {
//				flag = 'F';
//				msg = '입고창고를 입력해주세요.';
//				break;
//			} 
//			else {
				flag = 'T';
				gridRecord[i].set('V_TYPE', 'I');
//			}
		}
		
		if(Ext.getCmp('V_GR_SL_CD').getValue() == null || Ext.getCmp('V_GR_SL_CD').getValue() == undefined || Ext.getCmp('V_GR_SL_CD').getValue() == '' ){
			flag = 'F';
			msg = '입고창고를 입력해주세요.';
		}
		
		if(flag == 'F'){
			Ext.Msg.alert('확인', msg);
			return;
		}
		
		var GR_DT = Ext.Date.format(Ext.getCmp('V_GR_DT').getValue(), 'Y-m-d');
		if(flag == 'T') {
			Ext.Msg.confirm('확인', '입고등록하시겠습니까?<br>입고일 : ' + GR_DT, function(btn) {
				if (btn == 'yes') {
					Ext.Ajax.request({
						url : 'sql/M_GR_TOT_HSPF2.jsp',
						method : 'post',
						params : {
							V_TYPE : 'IH',
							V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
							V_GR_DT: Ext.getCmp('V_GR_DT').getValue(),
							V_GR_SL_CD: Ext.getCmp('V_GR_SL_CD').getValue(),
							
						},
						success : function(response) {
							var GR_NO = response.responseText;
//							console.log(GR_NO);
							
							store.sync({ 
								params : {
									V_TYPE : 'SYNC',
									V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
									V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
									V_GR_DT: Ext.getCmp('V_GR_DT').getValue(),
									V_GR_SL_CD: Ext.getCmp('V_GR_SL_CD').getValue(),
									V_GR_NO: GR_NO
								},
								callback : function(records, operation, success) {
									var tbController = M_GR_TOT_HSPF2.app.getController("TbButtonController");
									tbController.selBtnClick();
									Ext.getCmp('V_GR_SL_CD').setValue('');
								}, 
								success: function() {
									
								}
							});
							
						}
					});
				}
			})
		} else {
			Ext.Msg.alert('확인', msg);
		}
		
    	
    },
	gridAddBtnClick : function(button, e, eOpts) {
		// var popup = Ext.create("B_COMP_HSPF.view.WinAddRow");
		// popup.show();
		// Ext.getCmp('rowCount').setValue(1);
	},

	gridDelBtnClick : function(button, e, eOpts) {
		var store = Ext.getStore('MyStore');
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();

		if (gridRecord.length > 0) {
			Ext.Msg.confirm('확인', '삭제하시겠습니까?', function(button) {
				if (button == 'yes') {
					for (var i = 0; i < gridRecord.length; i++) {
						if (gridRecord[i].data['V_TYPE'] == 'V') {
							gridRecord[i].data['V_TYPE'] = 'D';
						}
					}
					store.sync({
						params : {
							V_USR_ID : 'admin',// parent.Ext.getCmp('GUSER_ID').getValue(),
						},
						callback : function(records, operation, success) {
							store.reload();
						}
					});
				}
			});
		}
	},

	xlsxDownClick : function(button, e, eOpts) {
		var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
		var grid = Ext.getCmp('myGrid');
		grid.saveDocumentAs({
			type : 'xlsx',
			title : '국내상품입고등록', // 엑셀내타이틀
			fileName : currentDate + '.xlsx' // 엑셀파일이름
		});
	},

	myGridClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var store1 = Ext.getStore('MyStore1');
		var gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
		var ccArr = new Array();
		
		for(var i=0; i<gridRecord.length; i++) {
			if(!ccArr.includes(gridRecord[i].get('GR_NO'))) {
				ccArr.push(gridRecord[i].get('PO_NO') + gridRecord[i].get('PO_SEQ'));
			}
		}

		var GR_NO = '\'';
		
		for(var i=0; i<ccArr.length; i++) {
			GR_NO += ccArr[i] +'\',\'';
		}
		
		GR_NO = '(' + GR_NO.substr(0, GR_NO.length -2) + ')'; 

		
		if (record.get('GR_QTY') != 0 && (gridRecord.length > 0)) {

			store1.load({
				params : {
					V_TYPE : 'SD',
					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					V_MVMT_NO: GR_NO,
				},
				callback : function(records, operations, success) {

				}
			});
		}

		if (gridRecord.length == 0 || gridRecord[0].get('GR_QTY') == 0) {
			store1.removeAll();
		}
	}

});
