Ext.define('S_EX_BILL_TOT_HSPF.controller.PopController', {
    extend: 'Ext.app.Controller',
    stores : [ 'MyStore1', 'PopStore'],
    
    control: {
		"#w_selBtn1" : {
			click : 'w_selBtn1Click'
		},
		'#popGrid': {
			celldblclick: 'w_popGridDblClick'
		},
		"textfield[name=pop_search]": {
            specialkey: 'tfEnter'
        }
	},

	w_selBtn1Click: function(button, e, eOpts) {
		var popStore = this.getPopStoreStore();
		popStore.load({
    		params: {
    			V_TYPE: 'P',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_DN_DT_FR : Ext.getCmp('W_DN_DT_FR').getValue(),
				V_DN_DT_TO : Ext.getCmp('W_DN_DT_TO').getValue(),
				V_S_BP_CD : Ext.getCmp('W_S_BP_NM2').getValue(),
    		},
    		callback: function(records,operations,success){
    		}
    	});
	},
	
	w_popGridDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		Ext.getCmp('V_BILL_NO').setValue(record.get('BILL_NO'));

    	var store1 = this.getMyStore1Store();
    	store1.removeAll();
    	
    	Ext.Ajax.request({
			url : 'sql/S_EX_BILL_TOT_HSPF.jsp',
			method : 'post',
			params: {
				V_TYPE: 'S',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				V_BILL_NO : record.get('BILL_NO'),
			},
			success: function(response) {
				var jsonResult = Ext.JSON.decode(response.responseText);
		    	var resultData = jsonResult.resultList[0];
		    	
		    	if(resultData != null){
					Ext.getCmp('V_S_BP_CD2').setValue(resultData.S_BP_CD);
					Ext.getCmp('V_S_BP_NM2').setValue(resultData.S_BP_NM);
					Ext.getCmp('V_R_BP_CD').setValue(resultData.S_BP_CD);
					Ext.getCmp('V_R_BP_NM').setValue(resultData.R_BP_NM);
					Ext.getCmp('V_IN_TERMS').setValue(resultData.IN_TERMS);
					
					Ext.getCmp('V_BILL_NO').setValue(resultData.BILL_NO);
					Ext.getCmp('V_BILL_DT').setValue(resultData.BILL_DT);
					Ext.getCmp('V_SALE_TYPE').setValue(resultData.SALE_TYPE);
					Ext.getCmp('V_DN_ISSUE_DT').setValue(resultData.DN_ISSUE_DT);
					Ext.getCmp('V_TAX_CD').setValue(resultData.TAX_CD);
					
					Ext.getCmp('V_CUR').setValue(resultData.CUR);
					Ext.getCmp('V_PAY_METH').setValue(resultData.PAY_METH);
	//				Ext.getCmp('V_VAT_INC').setValue(resultData.VAT_INC);
					Ext.getCmp('V_VAT_INC').items.get(resultData.VAT_INC == 'Y' ? 0 : 1).setValue(true);
	//				Ext.getCmp('V_CFM_YN').setValue(resultData.CFM_YN);
					Ext.getCmp('V_CFM_YN').items.get(resultData.CFM_YN == 'Y' ? 0 : 1).setValue(true);
					Ext.getCmp('V_VAT_TYPE').setValue(resultData.VAT_TYPE);
					Ext.getCmp('V_TO_SALES_GRP').setValue(resultData.TO_SALES_GRP);
					Ext.getCmp('V_XCHG_RT').setValue(resultData.XCHG_RT);
					
					Ext.getCmp('V_TEMP_GL_NO').setValue(resultData.TEMP_GL_NO);
					Ext.getCmp('V_REF_BILL_NO').setValue(resultData.REF_BILL_NO);
					
	    			if(resultData.TEMP_GL_NO != undefined) {	
						Ext.getCmp('cfmBtn').setDisabled(true);
//						Ext.getCmp('gridDelBtn').setDisabled(true);
						Ext.getCmp('tempGlCfmBtn').setDisabled(true);
						Ext.getCmp('tempGlCancelBtn').setDisabled(false);
					} else {
						Ext.getCmp('cfmBtn').setDisabled(false);
						Ext.getCmp('gridDelBtn').setDisabled(false);
						Ext.getCmp('tempGlCfmBtn').setDisabled(false);
						Ext.getCmp('tempGlCancelBtn').setDisabled(true);
					}
	    			
	    			store1.load({
			    		params: {
			    			V_TYPE: 'SD',
			    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
							V_BILL_NO : record.get('BILL_NO'),
			    		},
			    		callback: function(records,operations,success){
			    		}
					});
		    	}
		
				var popWin=  Ext.getCmp('WinBillPop');
				popWin.destroy();
			}
    	});
				
	},
	
	tfEnter: function(field, e, eOpts) {
    	if (e.getKey() == e.ENTER) {
    		this.w_selBtn1Click();
    	}
	}
	
});
