Ext.define('B_BIZ_ADD_HSPF.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',
    
    stores: [
        'MyStore'
    ],

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
    	var count = store.getCount();
    	var rowCount = Number(Ext.getCmp('rowCount').getValue());
    	
    	for(var i=0; i<rowCount; i++) {
    		count = count + 1;
    		var rec = Ext.create('B_BIZ_ADD_HSPF.model.MyModel', {
    			V_TYPE : 'I',
    			BP_CD : Ext.getCmp('V_BP_CD').getValue(),
    			BP_NM : Ext.getCmp('V_BP_NM').getValue(),
//    			BP_PUR_TYPE : 'DO',
//    			BP_SALE_TYPE : 'DSO',
//    			PUR_PAY_METH : 'CD',
//    			PUR_INCOTERMS : 'AON',
//    			SALE_PAY_METH : 'CD',
//    			SALE_INCOTERMS : 'AON',
//    			VAT_TYPE : 'A',
//    			USE_YN : 'N',
    			
    			
    		});
    		store.insert(count-1, rec);
    	}
    	
    	store.sync({
    		params:{
    			V_TYPE : 'SYNC',
    			V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
                V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
    		},
    		callback: function(records, operation, success) {
    			store.reload();
            },
    	});

    	var win = Ext.WindowManager.getActive();
    	if(win){
    	     win.close();
    	}
    }

});
