Ext.define('B_ITEM_SALE_HSPF.controller.WinAddRowController', {
    extend: 'Ext.app.Controller',

    control: {
        "#addRowBtn": {
            click: 'addRowBtnClick'
        }
    },

    addRowBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore');
    	var count = store.getCount();
    	var rowCount = Number(Ext.getCmp('rowCount').getValue());
    	
    	var now = new Date();
    	
    	var year = now.getFullYear();
    	var month = now.getMonth() + 1;
    	var day = now.getDate();
    	
    	var valid_dt = year + '-' + month + '-' + day;
    	
    	if (month < 10){
    		month = '0' + month;
    	}
    	if (day < 10){
    		day = '0' + day;
    	}

    	for(var i=0; i<rowCount; i++) {
    		count = count + 1;
    		var rec = Ext.create('B_ITEM_SALE_HSPF.model.MyModel', {
    			//USR_ID: parent.Ext.getCmp('GUSER_ID').getValue()
    			VALID_DT : valid_dt,
    		});
    		store.insert(i, rec);
    	}

    	var popWin=  Ext.getCmp('WinAddRow');
    	popWin.destroy();
    	
    	Ext.getCmp('myGrid').getView().focusRow(0);
    }

});
