Ext.define('B_STORAGE_LOCATION_HSPF.controller.MyGrid2Controller', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore2'
    ],

    control: {
        "#grid2AddBtn": {
            click: 'grid2AddBtnClick'
        },
        "#grid2DelBtn": {
            click: 'grid2DelBtnClick'
        },
        "#grid2SaveBtn": {
        	click: 'grid2SaveBtnClick'
        },
        "#xlsxDown2": {
            click: 'xlsxDown2Click'
        },
    },

    grid2AddBtnClick: function(button, e, eOpts) {
        var popup = Ext.create("B_STORAGE_LOCATION_HSPF.view.WinAddRow2");
        popup.show();
        Ext.getCmp('rowCount2').setValue(1);
    },

    grid2DelBtnClick: function(button, e, eOpts) {
        var store = Ext.getStore('MyStore2');
            	var gridRecord = Ext.getCmp('myGrid2').getSelectionModel().getSelection();

            	if(gridRecord.length > 0) {
        			Ext.Msg.confirm('확인', '삭제하시겠습니까?', function(button) {
        				if (button == 'yes') {
        					for (var i = 0; i < gridRecord.length; i++) {
        						if (gridRecord[i].data['V_TYPE'] == 'V') {
        							gridRecord[i].set('V_TYPE', 'RD');
        						}
        					}
        					store.sync({
        						params : {
        							V_TYPE : 'SYNC',
        							V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
        							V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
        						},
        						callback : function(records, operation, success) {
        							store.reload();
        						}
        					});
        				}
        			});
        		}
    },
    
    grid2SaveBtnClick: function(button, e, eOpts) {
		var store2 = Ext.getStore('MyStore2');
		var gridRecord = Ext.getCmp('myGrid2').getSelectionModel().getSelection();
		var flag = '';
		var msg = '';
		
		if (gridRecord.length > 0) {
		for (var i = 0; i < gridRecord.length; i++) {
			if (gridRecord[i].data['SL_CD'] == undefined) {
				flag = 'F';
				msg = '창고코드를 입력하세요.';
			} else if (gridRecord[i].data['LC_CD'] == undefined) {
				flag = 'F';
				msg = 'LOCATION코드를 입력하세요.';
			} else if (gridRecord[i].data['RACK_CD'] == undefined) {
				flag = 'F';
				msg = 'RACK코드를 입력하세요.';
			} else if (gridRecord[i].data['RACK_NM'] == undefined) {
				flag = 'F';
				msg = 'RACK명을 입력하세요.';
			} else if (gridRecord[i].data['MIN_QTY'] == undefined) {
				flag = 'F';
				msg = '최소수량을 입력하세요.';
			} else if (gridRecord[i].data['MAX_QTY'] == undefined) {
				flag = 'F';
				msg = '최대수량을 입력하세요.';
			} else {
				flag = 'T';
				if (gridRecord[i].data['V_TYPE'] == 'V') {
					gridRecord[i].set('V_TYPE', 'RU');
				}
			}
		}

			if (flag == 'T') {
				store2.sync({
					params : {
						V_TYPE : 'SYNC',
						V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
						V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					},
					callback : function(records, operation, success) {
						Ext.toast({
    	    				title: ' ',
    	    				timeout: 1500,
    	    				html: '저장완료',
    	    				style: 'text-align: center',
    	    				align: 't',
    	    				width: 130,
    	    			});
						store2.reload();
						
					}
				});
			} else {
				Ext.Msg.alert('확인', msg);
			}
		} else {
			Ext.Msg.alert('확인', '저장할 내역을 선택하세요.');
		}
	},

    xlsxDown2Click: function(button, e, eOpts) {
        var currentDate = Ext.util.Format.date(new Date(), 'Y-m-d His');
            	var grid = Ext.getCmp('myGrid2');
            	grid.saveDocumentAs({
                    type: 'xlsx',
                    title: 'test', //엑셀내타이틀
                    fileName: currentDate+'.xlsx' //엑셀파일이름
        		});
    },


});
