Ext.define('LOGIS_BILL.controller.TbButtonController', {
    extend: 'Ext.app.Controller',

    stores: [
        'MyStore'
    ],

    control: {
        "#selBtn": {
            click: 'selBtnClick'
        },
        "#saveBtn": {
            click: 'saveBtnClick'
        },
        "#delBtn": {
            click: 'delBtnClick'
        },
        "#clsBtn": {
            click: 'clsBtnClick'
        },
        "mysearchform textfield[name=search_field]": {
            specialkey: 'tfEnter'
        }
    },

    selBtnClick: function(button, e, eOpts) {
        //alert('select');
        var store = this.getMyStoreStore();
        store.removeAll();
        store.load({
            params: {
                V_TYPE: 'S',
                V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
                V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
                V_DTM_2380_FROM : Ext.getCmp('V_DTM_2380_FROM').getValue(),
                V_DTM_2380_TO : Ext.getCmp('V_DTM_2380_TO').getValue(),
                V_BGM_1004 : Ext.getCmp('V_BGM_1004').getValue(),
                V_NADII3036A : Ext.getCmp('V_NADII3036A').getValue(),
                V_RFF_VA : Ext.getCmp('V_RFF_VA').getValue(),
                V_RFF_VA_A : Ext.getCmp('V_RFF_VA_A').getValue(),
                V_RFF_VA_B : Ext.getCmp('V_RFF_VA_B').getValue(),
                V_RFF_VA_C : Ext.getCmp('V_RFF_VA_C').getValue(),
                V_RFF_CNO : Ext.getCmp('V_RFF_CNO').getValue(),
                V_RFF_CNO_A : Ext.getCmp('V_RFF_CNO_A').getValue(),
                V_RFF_CNO_B : Ext.getCmp('V_RFF_CNO_B').getValue(),
                V_BL_NO : Ext.getCmp('V_BL_NO').getValue(),
                V_LC_NO : Ext.getCmp('V_LC_NO').getValue(),

                V_HIDE_ERP : Ext.getCmp('V_HIDE_ERP').getValue(),

                V_REC_NM : Ext.getCmp('V_REC_NM').getValue(),

            },
            callback: function(records,operations,success){
            }
        });
    },

    saveBtnClick: function(button, e, eOpts) {
        alert('save');
    },

    delBtnClick: function(button, e, eOpts) {
        alert('delete');
    },

    clsBtnClick: function(button, e, eOpts) {
        var tab=parent.Ext.getCmp('myTab');
        var activeTab=tab.getActiveTab();
        var tabIndex=tab.items.indexOf(activeTab);
        tab.remove(tabIndex,true);
    },

    tfEnter: function(field, e, eOpts) {
        	if (e.getKey() == e.ENTER) {
        		this.selBtnClick();
        	}
    }

});
