Ext.define('Popup.controller.WinItemGrpPopController', {
    extend: 'Ext.app.Controller',
    stores: ['WinItemGrpPopStore'],
    control: {
        "#itemGrpSelBtn": {
            click: 'itemGrpSelBtnClick'
        },
		"#ItemGrpGrid" : {
			beforecelldblclick : 'cellDblClick'
		},
		"winitemgrppop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
    },

    itemGrpSelBtnClick: function(button, e, eOpts) {
    	var store = this.getWinItemGrpPopStoreStore();
		store.removeAll();
		store.load({
			params : {
				V_TYPE : 'S',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_ITEMGRP_CD : Ext.getCmp('W_ITEMGRP_CD').getValue(),
				W_ITEMGRP_NM : Ext.getCmp('W_ITEMGRP_NM').getValue()
			},
			callback : function(records, operations, success) {
			}
		});
    },
    
	cellDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var ITEM_GROUP_CD = record.get('ITEM_GROUP_CD');
		var gridRecord =  Ext.getCmp('myGrid');
		gridRecord.actionables['0'].context.record.set('ITEM_GROUP_CD', ITEM_GROUP_CD);
		
		var popWin=  Ext.getCmp('WinItemGrpPop');
		popWin.destroy();
		
	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.itemGrpSelBtnClick();
		}
	},

});
