Ext.define('Popup.controller.WinItemPopController', {
    extend: 'Ext.app.Controller',
    
    views: ['WinItemPop'],
	stores: ['WinItemPopStore'],
	control : {
		"#itemSelBtn": {
            click: 'itemSelBtnClick'
		},
		"#WinItemGrid" : {
			beforecelldblclick : 'cellDblClick'
		},
		"winitempop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},
    
    itemSelBtnClick: function(button, e, eOpts) {
		var store = this.getWinItemPopStoreStore();
		store.removeAll();
		
		store.load({
			params : {
				V_TYPE : 'S',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_ITEM_CD : Ext.getCmp('W_ITEM_CD').getValue(),
				W_ITEM_NM : Ext.getCmp('W_ITEM_NM').getValue(),
				W_M_BP_CD : Ext.getCmp('W_M_BP_CD').getValue(),
				W_TYPE : Ext.getCmp('W_TYPE').getValue(),
			},
			callback : function(records, operations, success) {
			}
		});
	},
	
	cellDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var ITEM_CD = record.get('W_ITEM_CD');
		var ITEM_NM = record.get('W_ITEM_NM');
		var SPEC = record.get('W_SPEC');
		var gridRecord;
		
		//발주등록 우측그리드
		if(Ext.getCmp('W_TYPE').getValue() == 'PO') {
			gridRecord = Ext.getCmp('myGrid2').getSelectionModel().getSelection();
			Ext.Ajax.request({
				url:'/HSPF01/mm/po/m_po_hspf/sql/M_PO_HSPF_D.jsp',
				method: 'post',
				params: {
					V_TYPE: 'B', //확정
					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					V_ITEM_CD : ITEM_CD,
					V_M_BP_CD : Ext.getCmp('W_M_BP_CD').getValue()
				},
				success : function(response) {
					var result = Ext.JSON.decode(response.responseText);
					gridRecord[0].set('ITEM_CD', ITEM_CD);
					gridRecord[0].set('ITEM_NM', ITEM_NM);
					gridRecord[0].set('PO_PRC', result.resultList[0].MB_PRC);
					gridRecord[0].set('MIN_PO_QTY', result.resultList[0].MIN_PO_QTY);
				},
				scope: this
			});
			
		}
		else if(Ext.getCmp('W_TYPE').getValue() == 'PO_DISTB') {
			gridRecord = Ext.getCmp('myGrid').getSelectionModel().getSelection();
			
			Ext.Ajax.request({
				url:'/HSPF01/HSPF02/mm2/po/m_po_distb/sql/M_PO_HSPF_DISTR_D.jsp',
				method: 'post',
				params: {
					V_TYPE: 'B', //확정
					V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
					V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
					V_ITEM_CD : ITEM_CD,
				},
				success : function(response) {
					var result = Ext.JSON.decode(response.responseText);
					gridRecord[0].set('ITEM_CD', ITEM_CD);
					gridRecord[0].set('ITEM_NM', ITEM_NM);
					gridRecord[0].set('PO_PRC', result.resultList[0].PO_PRC);
					gridRecord[0].set('UNIT', result.resultList[0].UNIT);
				},
				scope: this
			});
			
		} else if(Ext.getCmp('W_TYPE').getValue() == 'B_ITEM_SUPP_HSPF' //매입처단가등록
				||Ext.getCmp('W_TYPE').getValue() == 'B_ITEM_SALE_HSPF' //판매처단가등록
					||Ext.getCmp('W_TYPE').getValue() == 'PO_STEEL' //철강 발주 품목
						||Ext.getCmp('W_TYPE').getValue() == 'ITEM_STEEL' //철강 고철
					){ 
			var gridRecord =  Ext.getCmp('myGrid');
			gridRecord.actionables['0'].context.record.set('ITEM_CD',ITEM_CD);
			gridRecord.actionables['0'].context.record.set('ITEM_NM',ITEM_NM);
			if(Ext.getCmp('W_TYPE').getValue() == 'B_ITEM_SALE_HSPF'){
				gridRecord.actionables['0'].context.record.set('BP_ITEM_CD',ITEM_CD);
				gridRecord.actionables['0'].context.record.set('BP_ITEM_NM',ITEM_NM);
			}
		} else if(Ext.getCmp('W_TYPE').getValue() == 'S_RETURN_HSPF'){ //출고반품등록
			var gridRecord =  Ext.getCmp('myGrid');
			Ext.getCmp('V_ITEM_CD').setValue(ITEM_CD);
		} else if(Ext.getCmp('W_TYPE').getValue() == 'S_DN_REQ_HSPF'){ //출고요청등록
			Ext.getCmp('V_ITEM_CD').setValue(ITEM_CD);
			Ext.getCmp('V_ITEM_CD_RIGHT').setValue(ITEM_CD);
		} else if(Ext.getCmp('W_TYPE').getValue() == 'M_GR_TO_DN_TOT_HSPF'){ //입고기준출고등록
			Ext.getCmp('V_ITEM_CD').setValue(ITEM_CD);
		}  else if(Ext.getCmp('W_TYPE').getValue() == 'S_DN_REQ_HSPF_GRID'){ //출고요청등록 - GRID
			
			var grid =  Ext.getCmp('myGrid1');
			//			var record = grid.getSelectionModel().getSelection();	
			var record = grid.getStore().getRange();
			var rowIndex = Ext.getCmp('rowIndex').getValue();
			record[rowIndex].set('ITEM_CD', ITEM_CD);
			record[rowIndex].set('ITEM_NM', ITEM_NM);
			record[rowIndex].set('SPEC', SPEC);
		} else if(Ext.getCmp('W_TYPE').getValue() == 'IV_STEEL' || Ext.getCmp('W_TYPE').getValue() == 'IV_DISTB') {
			var gridRecord =  Ext.getCmp('myGrid1');
			gridRecord.actionables['0'].context.record.set('ITEM_CD', ITEM_CD);
			gridRecord.actionables['0'].context.record.set('ITEM_NM', ITEM_NM);
		} 
		else if (Ext.getCmp('W_TYPE').getValue() == 'FORM_ITEM_NM'){
			Ext.getCmp('V_ITEM_NM').setValue(ITEM_NM);
		}
		else if (Ext.getCmp('W_TYPE').getValue() == 'FORM_ITEM_CD'){
			Ext.getCmp('V_ITEM_CD').setValue(ITEM_CD);
		}
		else if (Ext.getCmp('W_TYPE').getValue() == 'FORM_ITEM_NM_CD'){
			Ext.getCmp('V_ITEM_NM').setValue(ITEM_NM);
			Ext.getCmp('V_ITEM_CD').setValue(ITEM_CD);
		}
		else {
			var gridRecord =  Ext.getCmp('myGrid').editingPlugin.getEditor();
			var index = gridRecord.activeField.column.fullColumnIndex;
			
			gridRecord.items.items[index].setValue(ITEM_CD);
			gridRecord.items.items[index+1].setValue(ITEM_NM);
		}
		var popWin=  Ext.getCmp('WinItemPop');
		popWin.destroy();

	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.itemSelBtnClick();
		}
	},

});
