Ext.define('Popup.controller.WinSBpPopController', {
	extend : 'Ext.app.Controller',

	views: ['WinSBpPop'],
	stores: ['WinSBpPopStore'],
	control : {
		"#bpSelBtn" : {
			click : 'sbpSelBtnClick'
		},
		"#SbpSelBtn" : {
			click : 'sbpSelBtnClick1'
		},
		"#WinSBpGrid" : {
			beforecelldblclick : 'cellDblClick'
		},
		"winsbppop textfield[name=search_field]" : {
			specialkey : 'tfEnter'
		}
	},

	sbpSelBtnClick : function(button, e, eOpts) {
		var store = this.getWinSBpPopStoreStore();
		store.removeAll();
		store.load({
			params : {
				V_TYPE : 'S',
				V_COMP_ID : parent.Ext.getCmp('GCOMP_ID').getValue(),
				V_USR_ID : parent.Ext.getCmp('GUSER_ID').getValue(),
				W_S_BP_CD : Ext.getCmp('W_S_BP_CD').getValue(),
				W_S_BP_NM : Ext.getCmp('W_S_BP_NM').getValue()
			},
			callback : function(records, operations, success) {
			}
		});
	},

	cellDblClick : function(tableview, td, cellIndex, record, tr, rowIndex, e, eOpts) {
		var S_BP_CD = record.get('S_BP_CD');  
		var S_BP_NM = record.get('S_BP_NM'); 
		
		if(Ext.getCmp('fieldType').getValue() == 'textfield') {
			Ext.getCmp('V_S_BP_CD').setValue(S_BP_CD);
			Ext.getCmp('V_S_BP_NM').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'textfield2') { //수주등록의 고객사팝업
			Ext.getCmp('V_BP_CD').setValue(S_BP_CD);
			Ext.getCmp('V_BP_NM').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'textfield3') {  //수주조회팝업의 고객사팝업
			Ext.getCmp('W_S_BP_CD_SO').setValue(S_BP_CD);
			Ext.getCmp('W_S_BP_NM_SO').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'textfield4') {  //출고요청등록의 고객사팝업
			Ext.getCmp('V_BP_CD').setValue(S_BP_CD);
			Ext.getCmp('V_BP_NM').setValue(S_BP_NM); 
			Ext.getCmp('V_BP_CD_RIGHT').setValue(S_BP_CD);
			Ext.getCmp('V_BP_NM_RIGHT').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_PUR_HSPF' || //발주요청조회
			Ext.getCmp('fieldType').getValue() == 'S_SO_QUERY_HSPF'|| //수주조회
			Ext.getCmp('fieldType').getValue() == 'ST_ITEM_DISTB'|| //품목별재고현황
			Ext.getCmp('fieldType').getValue() == 'ST_ITEM_STEEL'||
			Ext.getCmp('fieldType').getValue() == 'S_RETURN_HSPF') { //출고반품등록
			Ext.getCmp('V_S_BP_NM').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'B_ITEM_SALE_HSPF') { //매출처단가등록-그리드
			var gridRecord =  Ext.getCmp('myGrid');
			gridRecord.actionables['0'].context.record.set('BP_CD', S_BP_CD);
			gridRecord.actionables['0'].context.record.set('BP_NM', S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'B_ITEM_SALE_HSPF_tf' ||  //매출처단가등록-검색
				  Ext.getCmp('fieldType').getValue() == 'M_PROD_DN_HSPF_tf' || //생산출고등록 
				  Ext.getCmp('fieldType').getValue() == 'S_DN_PAPER' || //출하증작성
				  Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_DISTR' || //입고기준출고등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'S_BILL_DISTR' || //매출채권등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'S_TAX_DISTR' || //세금계산서등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_STEEL' || //입고기준출고등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'S_BILL_STEEL' || //매출채권등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'S_TAX_STEEL' ||
				  Ext.getCmp('fieldType').getValue() == 'S_DD_CFM_HSPF') { //출고지시확정 
			Ext.getCmp('V_S_BP_NM').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_PO_HSPF') { //발주등록 - 직입고
			Ext.getCmp('V_S_BP_CD').setValue(S_BP_CD +', '+ S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'S_BILL_DISTR_W' || //매출채권등록팝업
				Ext.getCmp('fieldType').getValue() == 'S_TAX_DISTR_W'||
				Ext.getCmp('fieldType').getValue() == 'S_TAX_STEEL_W' ||
				Ext.getCmp('fieldType').getValue() == 'S_BILL_STEEL_W'
					) { // 세금계산서팝업
			Ext.getCmp('W_S_BP_NM').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_PO_DISTB_W'||
				Ext.getCmp('fieldType').getValue() == 'M_COL_DISTB_W'||
				Ext.getCmp('fieldType').getValue() == 'M_PO_STEEL_W'||
				Ext.getCmp('fieldType').getValue() == 'M_COL_STEEL_W') { //발주등록(일반무역-팝)
			Ext.getCmp('W_S_BP_NM2').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_PO_DISTB_W3' || Ext.getCmp('fieldType').getValue() == 'M_PO_STEEL_W3') { //발주등록(일반무역-팝)
			Ext.getCmp('W_S_BP_NM3').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'S_DN_REQ_HSPF_L' ||
				  Ext.getCmp('fieldType').getValue() == 'M_PO_DISTB' ||  //발주등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'Y_PUMS_DISTR' ||  //리스크관리(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'Y_APPROV_DISTR' ||  //품의등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'M_PO_STEEL' ||  //발주등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'Y_PUMS_STEEL' ||  //리스크관리(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'Y_APPROV_STEEL' ||  //품의등록(일반무역)
				  Ext.getCmp('fieldType').getValue() == 'END_VS_ERP_DN_HSPF' ) { 
			Ext.getCmp('V_S_BP_NM').setValue(S_BP_NM);
			Ext.getCmp('V_S_BP_CD').setValue(S_BP_CD);
		} else if(Ext.getCmp('fieldType').getValue() == 'S_DN_REQ_HSPF_R') { //출고요청등록 - 오른쪽
			Ext.getCmp('V_BP_NM_RIGHT').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_DISTR2' || Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_STEEL2') { //매출채권등록(일반무역)
			Ext.getCmp('V_S_BP_NM2').setValue(S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_DISTR2' ||//입고기준출고등록(일반무역)
				Ext.getCmp('fieldType').getValue() == 'S_BILL_DISTR2' ||
				Ext.getCmp('fieldType').getValue() == 'S_BILL_STEEL2'		
		) { //매출채권등록(일반무역)
			Ext.getCmp('V_S_BP_CD2').setValue(S_BP_CD);
			Ext.getCmp('V_S_BP_NM2').setValue(S_BP_NM);
		} else if((Ext.getCmp('fieldType').getValue() == 'Q_BASE_METHOD_HSPF') ||
					(Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_DISTR_G') ||
					(Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_STEEL_G') ||// 입고기준출고등록(일반무역)
					(Ext.getCmp('fieldType').getValue() == 'B_STORAGE_STEEL') ||
			(Ext.getCmp('fieldType').getValue() == 'B_STORAGE_DISTR')) {// 창고기준정보(일반무역)
			var grid =  Ext.getCmp('myGrid');
			var record = grid.getSelectionModel().getSelection();
			record[0].set('S_BP_CD', S_BP_CD);
			record[0].set('S_BP_NM', S_BP_NM);
		} else if(Ext.getCmp('fieldType').getValue() == 'S_DN_REQ_HSPF'){// 출고요청등록 - 고객사
			var grid =  Ext.getCmp('myGrid1');
			var record = grid.getSelectionModel().getSelection();
			var rowIndex = Ext.getCmp('rowIndex').getValue();
//			record[rowIndex].set('S_BP_CD', S_BP_CD);
//			record[rowIndex].set('S_BP_NM', S_BP_NM);
			
			var store = grid.getStore();
			store.each(function(record, index) {
				record.set('S_BP_CD', S_BP_CD);
				record.set('S_BP_NM', S_BP_NM);
			})
		}
		else if(Ext.getCmp('fieldType').getValue() == 'grid'){
			var grid =  Ext.getCmp('myGrid');
			var record = grid.getSelectionModel().getSelection();
			record[0].set('S_BP_CD', S_BP_CD);
			record[0].set('S_BP_NM', S_BP_NM);
	}
	else if(Ext.getCmp('fieldType').getValue() == 'grid1'){
			var grid =  Ext.getCmp('myGrid1');
			var record = grid.getSelectionModel().getSelection();
			record[0].set('S_BP_CD', S_BP_CD);
			record[0].set('S_BP_NM', S_BP_NM);
		}
	else if(Ext.getCmp('fieldType').getValue() == 'M_GR_TO_DN_TOT_HSPF'){
		var record = Ext.getCmp('myGrid').getStore().getAt(Ext.getCmp('DBCLICK_ROW_INDEX').getValue());
		
		record.set('S_BP_CD', S_BP_CD);
		record.set('S_BP_NM', S_BP_NM);
		
		var selModel= Ext.getCmp('myGrid').getSelectionModel();
		selModel.select(record, true);
	}
		else {
			var S_BP_CD = record.get('S_BP_CD');
			var S_BP_NM = record.get('S_BP_NM');
			var gridRecord =  Ext.getCmp('myGrid').editingPlugin.getEditor();
			var index = gridRecord.activeField.column.fullColumnIndex;
			
			gridRecord.items.items[index].setValue(S_BP_CD);
			gridRecord.items.items[index+1].setValue(S_BP_NM);
		};
		
		var popWin=  Ext.getCmp('WinSBpPop');
		popWin.destroy();
	},

	tfEnter : function(field, e, eOpts) {
		if (e.getKey() == e.ENTER) {
			this.sbpSelBtnClick();
		}
	},
	
});
